            }
            $(".Blog").append(div+code);
          }

          var p = $(".pagination");
          var pitems = $(".pagination-item");
          var activeElem = $(".active-page");
          var lp = pitems.eq(pitems.length-1);
          var next = activeElem.next().attr("href");
