function stripHtmlTags(s, max) {
    return s.replace(/<.*?>/ig, '').split(/\s+/).slice(0, max - 1).join(' ')
}

function createSummaryAndThumb(pID, title, url, date, comment, author, tag, authurl, authorphoto) {
    var posturl = url;
    var authorurl = authurl;
    var title = title;
    var date = date;
    var comment = comment;
    var tag = tag;
    var div = document.getElementById(pID);
    var img = div.getElementsByTagName("img");
    var content1 = div.innerHTML.replace(/<img.*?>/ig, '').replace(/<iframe.*?>/ig, '');
    var month = new Array();
    month[1] = "Jan";
    month[2] = "Feb";
    month[3] = "Mar";
    month[4] = "Apr";
    month[5] = "May";
    month[6] = "Jun";
    month[7] = "Jul";
    month[8] = "Aug";
    month[9] = "Sep";
    month[10] = "Oct";
    month[11] = "Nov";
    month[12] = "Dec";
    var n = month[date.split('/')[0]];
    var date1 = date.split('/')[1];
    var year = date.split('/')[2];
    var yearapos = "'";
    yearshort = yearapos + year.slice(-2);
    var thumb1;
    var authorphoto1 = "<img src=" + authorphoto + " width='50px'/>";
    if (img.length == 1) {
        var imgurl = img[0].src;
        var thumb = '<div class="post-image"><a href="' + posturl + '"><img class="thumb" width="940" height="987" src="' + imgurl + '"></a></div>';
        var summary1 = '<div class="embedd"><header class="post-header"><h2 class="entry-title"><a href=' + posturl + '>' + title + '</a></h2></header><div class="cen"><div class="post-details"><span class="auth"><div class="labelhome">' + tag + '</div></span> </div> </div></div>' + thumb + '<div class="entry-content clear"><div class="intro-text">' + stripHtmlTags(content1, 45) + '...</div><div class="post-footer"><div class="pull-right"><span class="authorprofile">' + authorphoto1 + '</span><span class="authorprofileright"><a href="' + authurl + '">' + author + '</a><span class="post-da">, &nbsp;' + date1 + " " + n + " " + yearshort + '</span></span></div><div class="pull-left"><a class="read-more" href="' + posturl + '">Read More <i class="fa fa-angle-double-right"></i></a></div></div></div>'
    } else {
        if (img.length > 1) {
            var thum = '';
            for (var i = 0; i < img.length; i++) {
                thum = thum + '<div><img src="' + img[i].src + '"></div>'
            }
            var thumb = '<div class="slickslider">' + thum + '</div>';
            var summary1 = '<div class="embedd"><header class="post-header"><h2 class="entry-title"><a href=' + posturl + '>' + title + '</a></h2></header><div class="cen"><div class="post-details"><span class="auth"><div class="labelhome">' + tag + '</div></span> </div> </div></div>' + thumb + '<div class="entry-content clear"><div class="intro-text">' + stripHtmlTags(content1, 45) + '... </div><div class="post-footer"><div class="pull-right"><span class="authorprofile">' + authorphoto1 + '</span><span class="authorprofileright"><a href="' + authurl + '">' + author + '</a><span class="post-da">, &nbsp;' + date1 + " " + n + " " + yearshort + '</span></span></div><div class="pull-left"><a class="read-more" href="' + posturl + '">Read More <i class="fa fa-angle-double-right"></i></a></div></div></div>'
        } else {
