(function($){	
	var newerLink = $('a.blog-pager-newer-link1');
	var olderLink = $('a.blog-pager-older-link1');
	$.get(newerLink.attr('href'), function (data) {
	 newerLink.html('<h5>Next</h5><h6>'+$(data).find('.post h1.post-title').text()+'</h6>');	
	},"html");
	$.get(olderLink.attr('href'), function (data2) {
	 olderLink.html('<h5>Previous</h5><h6>'+$(data2).find('.post h1.post-title').text()+'</h6>');	
	},"html");
})(jQuery);
