                var summary1 = '<div class="entry-media ">' + thumb + '</div><div class="entry-post author"><div class="entry-meta"><span class="date">' + date + '</span><span class="categories">' + comment + ' Comments</span></div><h3 class="entry-title"><a href=' + posturl + '>' + title + '</a></h3><div class="entry-content clearfix"><p>' + stripHtmlTags(content, 20) + '...</p><p class="more-link clearfix"><a class=" btn-flat" href=' + posturl + '>Read more</a></p></div><div class="entry-author"><div class="author-avatar"><span class="fa fa-video-camera"></span></div><div class="author-name">' + tag + '</div><div class="footer-socials"><a href="http://www.facebook.com/sharer.php?u=' + posturl + '" title="Share on Facebook" target="_blank" class="fa fa-facebook"></a><a href="http://twitter.com/home?status=' + posturl + '" title="Share on Twitter" target="_blank" class="fa fa-twitter"></a><a href="https://plus.google.com/share?url=' + posturl + '" title="Share on Google+" target="_blank" class="fa fa-google-plus"></a></div></div></div>'
