function stripHtmlTags(s, max) {
    return s.replace(/<.*?>/ig, '').split(/\s+/).slice(0, max - 1).join(' ')
}

function postAndThumb(pID, title, url, date, comment, tag, author) {
    var posturl = url;
    var title = title;
    var date = date;
    var comment = comment;
    var tag = tag;
    var div = document.getElementById(pID);
    var img = div.getElementsByTagName("img");
    var content1 = div.innerHTML.replace(/<img.*?>/ig, '').replace(/<iframe.*?>/ig, '');
    var arr = content1.split(/<br\s*\/?>/);
    var content = arr[0] + arr.slice(1, -1).join('<br>') + arr.slice(-1);
    var month = new Array();
    month[0] = "Jan";
    month[1] = "Feb";
    month[2] = "Mar";
    month[3] = "Apr";
    month[4] = "May";
    month[5] = "Jun";
    month[6] = "Jul";
    month[7] = "Aug";
    month[8] = "Sep";
    month[9] = "Oct";
    month[10] = "Nov";
    month[11] = "Dec";
    var n = month[date.split('/')[0]];
    var date1 = date.split('/')[1];
    var year = date.split('/')[2];
    if (img.length == 1) {
        var imgurl = img[0].src;
        var thumb = '<div class="post-media"><div class="hider-page" style="display: none;"></div><a href="' + imgurl + '" class="image-wrap image-popup-no-margins"><img src="' + imgurl + '"><span class="zoom-icon"><i class="fa fa-search"></i></span></a></div>';
        var summary1 = '<div class="entry-media">' + thumb + '</div>'
    } else {
        if (img.length > 1) {
            var li = '';
            for (var i = 0; i < img.length; i++) {
                var li = li + '<a class="zoomer" title="' + img[i].alt + '" data-source="' + img[i].src + '" href="' + img[i].src + '"><img title="' + img[i].alt + '" alt="' + img[i].alt + '" src="' + img[i].src + '"/></a>'
            }
            var thumb = '<div class="zoom-gallery"><div class="zoom-gallery justifiedgall_xtkwsdjowo"> ' + li + '</div></div>';
            var summary1 = '<div class="entry-media">' + thumb + '</div>'
        } else {
            var frame = div.getElementsByTagName("iframe");
            if (frame.length >= 1) {
                var iframe1 = frame[0].src;
                var thumb = '<div class="entry-feature"><iframe width="100%" height="440" frameborder="no" src="' + iframe1 + '" scrolling="no"></iframe></div>';
                var summary1 = '<div class="entry-feature">' + thumb + '</div>'
            } else {
                var summary1 = '<div class="entry-article"></div>'
            }
        }
    }
    div.innerHTML = summary1;
    div.style.display = "block";
    var elem = document.getElementsByClassName("separator");
    for (var i = 0; i < elem.length; i++) {
        elem[i].innerHTML = ''
    }
}
