function ShowPost1(a) {
    (function(b) {
        var g = {
            blogURL: "",
            MaxPost: 5,
            idcontaint: "",
            FirstImageSize: 600,
            ImageSize: 300,
            Summarylength: 90,
            animated: false,
            loadingClass: "loadingz",
            pBlank: "",
            MonthNames: ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"],
            tagName: false
        };
        g = b.extend({}, g, a);
        var d = b(g.idcontaint);
        d.html('<div class="aitems"><ul class="slides"></ul></div>').addClass(g.loadingClass);
        b.get((g.blogURL === "" ? window.location.protocol + "//" + window.location.host : g.blogURL) + "/feeds/posts/summary" + (g.tagName === false ? "" : "/-/" + g.tagName) + "?max-results=" + g.MaxPost + "&orderby=published&alt=json-in-script", function(l) {
            var s, p, h, O, u, r, n, o, m, q, k, f, w, E, M = "",
                v = l.feed.entry;
            for (var t = 0; t < v.length; t++) {
                for (var e = 0; e < v[t].link.length; e++) {
                    if (v[t].link[e].rel == "alternate") {
                        s = v[t].link[e].href;
                        break
                    }
                }
                if ("media$thumbnail" in v[t]) {
                    u = v[t].media$thumbnail.url
                } else {
                    u = g.pBlank
                }
                if ("content" in v[t]) {
                    h = v[t].content.$t
                } else {
                    if ("summary" in v[t]) {
                        h = v[t].summary.$t
                    } else {
                        h = ""
                    }
                }
                h = h.replace(/<\S[^>]*>/g, "");
                if (h.length > g.Summarylength) {
                    h = h.substring(0, g.Summarylength) + "..."
                }
                if (v[t] === v[0]) {
                    u = u.replace(/\/s[0-9]+\-c/g, "/s" + g.FirstImageSize + "-p");
                    if (u.indexOf("img.youtube.com/vi/") != -1) {
                        u = u.replace("default", "0")
                    }
                    n = "<p>" + h + "</p>"
                } else {
                    u = u.replace(/\/s[0-9]+\-c/g, "/s" + g.ImageSize + "-p");
                    n = ""
                }
                p = v[t].title.$t;
                w = v[t].author[0].name.$t;
                E = v[t].category[0].term;
                f = v[t].published.$t.substring(0, 10);
                o = f.substring(0, 4);
                m = f.substring(5, 7);
                q = f.substring(8, 10);
                k = g.MonthNames[parseInt(m, 10) - 1];
                r = '<div class="caro-thumb"><a href="' + s + '"><div class="lar-thumb" style="background:url(' + u + ')center no-repeat;background-size:cover;"></div></a><div class="caro-content"><div class="post-fa-icon"><i class="fa fa-bookmark"></i><a href="/search/label/' + E + '">' + E + '</a></div><span class="entry-meta">' + f + ' </span><h3 class="entry-title"><a href="' + s + '">' + p + '</a></h3></div></div>';
                M += '<li class="slide-col" ><div class="caro-item">' + r + "</div></li>"
            }
            b("ul", d).append(M);
            if (g.animated) {
                b("ul", d).addClass("scroll-item");
                b(g.idcontaint + " .aitems").flexslider({
                    animation: "slide",
                    selector: ".scroll-item > li",
                    animationLoop: true,
                    pauseOnHover: true,
                    controlNav: false,
                    directionNav: true,
                    itemWidth: 320,
                    move: 1,
                    itemMargin: 0,
                    minItems: 1,
                    prevText: '<i class="fa fa-angle-left"></i>',
                    nextText: '<i class="fa fa-angle-right"></i>',
                    mousewheel: false,
                    maxItems: 3
                })
            } else {
                if (g.tagName != false) {
                    d.append('<div class="lab-title"><a href="/search/label/' + encodeURIComponent(g.tagName) + '?&max-results=7"><i class="fa fa-arrow-right"></i></a></div>')
                }
            }
            d.removeClass(g.loadingClass)
        }, "jsonp")
    })(jQuery)
}//

