        return false
    });
    $('#primary-menu > li.menu-item-has-children > a').append("<span><i class='fa'>+</i></span>");
    $('#primary-menu > li > ul').hide();
    $('#site-navigation ul li.menu-item-has-children > a').live('click', function() {
        if ($(this).next('.sub-menu').is(':visible')) {
            $(this).next('.sub-menu').slideUp();
            $(this).find('.fa').html('+');
            myScroll.refresh()
        } else {
            $(this).next('.sub-menu').slideDown();
            $(this).find('.fa').html('-');
            myScroll.refresh()
        }
    });
    $('#top-search .btn').live('click', function() {
        if ($(this).next('div').is(':visible')) {
            $(this).next('div').hide()
        } else {
            $(this).next('div').show()
        }
    });
    $(window).scroll(function() {
        if ($(this).scrollTop() > 400) {
            $('#scrollUp').fadeIn()
        } else {
            $('#scrollUp').fadeOut()
        }
    });
    $('#scrollUp').click(function() {
        $('html, body').animate({
            scrollTop: 0
        }, 800);
        return false
    })
