    $(".floating-buttons .social-icon").mouseenter(function() {
        $(this).stop();
        $(this).animate({
            width: "160"
        }, 500, "easeOutBounce", function() {})
    });
    $(".floating-buttons .social-icon").mouseleave(function() {
        $(this).stop();
        $(this).animate({
            width: "43"
        }, 500, "easeOutBounce", function() {})
    })
});
$(document).ready(function() {
    $("#nav-mobile").html($(".menu-secondary-container").html());
    $("#nav-trigger span").click(function() {
        if ($("nav#nav-mobile ul").hasClass("expanded")) {
            $("nav#nav-mobile ul.expanded").removeClass("expanded").slideUp(250);
            $(this).removeClass("open")
        } else {
            $("nav#nav-mobile ul").addClass("expanded").slideDown(250);
            $(this).addClass("open")
        }
    })
});

function FeaturedPostSide(j) {
    (function(y) {
        var G = {
            blogURL: "",
            MaxPost: 4,
            idcontaint: "",
            ImageSize: 100,
            interval: 5000,
            autoplay: false,
            loadingClass: "loadingxx",
            pBlank: "http://1.bp.blogspot.com/-htG7vy9vIAA/Tp0KrMUdoWI/AAAAAAAABAU/e7XkFtErqsU/s1600/grey.gif",
            MonthNames: ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"],
            tagName: false
        };
        G = y.extend({}, G, j);
        var q = y(G.idcontaint);
        var n = G.MaxPost * 200;
        q.html("<div class=\"sliderx\"><ul class=\"abt-sidebar-slider\"></ul></div><div class=\"buttons\"><a href=\"#\" class=\"prevx\">prev</a><a href=\"#\" class=\"nextx\">next</a></div>");
        var o = function(P) {
            var K, T, I, N, Q, J, M, O, L, H = "",
                t = P.feed.entry;
            for (var U = 0; U < t.length; U++) {
                for (var r = 0; r < t[U].link.length; r++) {
                    if (t[U].link[r].rel == "alternate") {
                        K = t[U].link[r].href;
                        break
                    }
                };
                if ("media$thumbnail" in t[U]) {
                    N = t[U].media$thumbnail.url.replace(/\/s[0-9]+\-c/g, "/s" + G.ImageSize + "-c")
                } else {
                    N = G.pBlank.replace(/\/s[0-9]+(\-c|\/)/, "/s" + G.ImageSize + "$1")
                };
                T = t[U].title.$t;
                L = t[U].published.$t.substring(0, 10);
                I = t[U].author[0].name.$t;
                Q = L.substring(0, 4);
                J = L.substring(5, 7);
                M = L.substring(8, 10);
                O = G.MonthNames[parseInt(J, 10) - 1];
                H += "<li><a target=\"_blank\" href=\"" + K + "\"><div class=\"overlayx\"></div><img class=\"random\" src=\"" + N + "\"/><h4>" + T + "</h4></a><div class=\"label_text\"><span class=\"date\"><span class=\"dd\">" + M + "</span> <span class=\"dm\">" + O + "</span> <span class=\"dy\">" + Q + "</span></span> <span class=\"autname\">" + I + "</span></div></li>"
            };
            y("ul", q).append(H).addClass(G.loadingClass)
        };
        var m = function() {
            y(G.idcontaint + " .nextx").click()
        };
        var l = function() {
            y.get((G.blogURL === "" ? window.location.protocol + "//" + window.location.host : G.blogURL) + "/feeds/posts/summary" + (G.tagName === false ? "" : "/-/" + G.tagName) + "?max-results=" + G.MaxPost + "&orderby=published&alt=json-in-script", o, "jsonp");
            setTimeout(function() {
                y(G.idcontaint + " .prevx").click(function() {
                    y(G.idcontaint + " .sliderx li:first").before(y(G.idcontaint + " .sliderx li:last"));
                    return false
                });
                y(G.idcontaint + " .nextx").click(function() {
                    y(G.idcontaint + " .sliderx li:last").after(y(G.idcontaint + " .sliderx li:first"));
                    return false
                });
                if (G.autoplay) {
                    var V = G.interval;
                    var W = setInterval(m, V);
                    y(G.idcontaint + " .sliderx li:first").before(y(G.idcontaint + " .sliderx li:last"));
                    y(G.idcontaint + " .sliderx").hover(function() {
                        clearInterval(W)
                    }, function() {
                        W = setInterval(m, V)
                    })
                };
                y("ul", q).removeClass(G.loadingClass)
            }, n)
        };
        y(document).ready(l)
    })(jQuery)
}
var defaultnoimage = "https://lh6.googleusercontent.com/-Fl0nYrOVF_s/UjK6m_-b_AI/AAAAAAAAGVc/sl7gYi5Cw88/s300-no/no_img.gif",
    maxresults = 3,
    splittercolor = "#d4eaf2",
    relatedpoststitle = "",
    relatedTitles = [],
    relatedTitlesNum = 0,
    relatedUrls = [],
    thumburl = [];

function related_results_labels_thumbs(o) {
    for (var y = 0; y < o.feed.entry.length; y++) {
        var q = o.feed.entry[y];
        relatedTitles[relatedTitlesNum] = q.title.$t;
        try {
            thumburl[relatedTitlesNum] = q.media$thumbnail.url
        } catch (h) {
            s = q.content.$t, a = s.indexOf("<img"), b = s.indexOf("src=\"", a), c = s.indexOf("\"", b + 5), d = s.substr(b + 5, c - b - 5), thumburl[relatedTitlesNum] = -1 != a && -1 != b && -1 != c && "" != d ? d : "undefined" !== typeof defaultnoimage ? defaultnoimage : "http://2.bp.blogspot.com/-QP4NqoWBg8Y/TZynybflhKI/AAAAAAAAAvM/7MRErFNCiFE/s1600/No-Image.jpg"
        };
        70 < relatedTitles[relatedTitlesNum].length && (relatedTitles[relatedTitlesNum] = relatedTitles[relatedTitlesNum].substring(0, 70) + "...");
        for (var T = 0; T < q.link.length; T++) {
            "alternate" == q.link[T].rel && (relatedUrls[relatedTitlesNum] = q.link[T].href, relatedTitlesNum++)
        }
    }
}

function removeRelatedDuplicates_thumbs() {
    for (var o = [], y = [], q = [], G = 0; G < relatedUrls.length; G++) {
        contains_thumbs(o, relatedUrls[G]) || (o.length += 1, o[o.length - 1] = relatedUrls[G], y.length += 1, q.length += 1, y[y.length - 1] = relatedTitles[G], q[q.length - 1] = thumburl[G])
    };
    relatedTitles = y;
    relatedUrls = o;
    thumburl = q
}

function contains_thumbs(o, y) {
    for (var q = 0; q < o.length; q++) {
        if (o[q] == y) {
            return !0
        }
    };
    return !1
}

function printRelatedLabels_thumbs(o) {
    for (var y = 0; y < relatedUrls.length; y++) {
        relatedUrls[y] != o && relatedTitles[y] || (relatedUrls.splice(y, 1), relatedTitles.splice(y, 1), thumburl.splice(y, 1), y--)
    };
    o = Math.floor((relatedTitles.length - 1) * Math.random());
    y = 0;
    0 < relatedTitles.length && document.write("<h2>" + relatedpoststitle + "</h2>");
    for (document.write("<div/>"); y < relatedTitles.length && 30 > y && y < maxresults;) {
        document.write("<div class=\"recent-item\"><div class=\"post-thumbnail\"><a class=\"arecent\" href=\"" + relatedUrls[o] + "\"><img style=\"position:absolute\" onload=\"OnImageLoad(event);\" class=\"img-related\" src=\"" + thumburl[o] + "\"/></a></div><h3 class=\"metatag\"><a href=\"" + relatedUrls[o] + "\">" + relatedTitles[o] + "</a></h3></div>"), y++, o < relatedTitles.length - 1 ? o++ : o = 0
    };
    document.write("</div>");
    relatedUrls.splice(0, relatedUrls.length);
    thumburl.splice(0, thumburl.length);
    relatedTitles.splice(0, relatedTitles.length)
}
var shuffleArray = function(j) {
        var l = j.length,
            m, n;
        if (0 === l) {
            return !1
        };
        for (; --l;) {
            m = Math.floor(Math.random() * (l + 1)), n = j[l], j[l] = j[m], j[m] = n
        };
        return j
    },
    getRandomInt = function(j, l) {
        return Math.floor(Math.random() * (l - j + 1)) + j
    };
