        if ($(this).scrollTop() > 200) {
            $('.to-top').fadeIn()
        } else {
            $('.to-top').fadeOut()
        }
    });


    $('.to-top').hide().click(function() {
        $('html, body').animate({
            scrollTop: 0
        }, 800);
        return false;
    });




