            } else {
                var frame = div.getElementsByTagName("iframe");
                if (frame.length >= 1) {
                    var iframe1 = frame[0].src;
                    var thumb = '<div class="post-image"><iframe width="100%" height="450" frameborder="no" src="' + iframe1 + '" scrolling="no"></iframe></div>';
