$(document).ready(function() {
    $('.post-image,.zoom-gallery').find('img').each(function(n, image) {
        var image = $(image);
        image.attr({
            src: image.attr('src').replace("s72", "s1600")
        });
        image.attr({
            src: image.attr('src').replace("s320", "s1600")
        });
        image.attr({
            src: image.attr('src').replace("s400", "s1600")
        });
        image.attr({
            src: image.attr('src').replace("s640", "s1600")
        })
    });
    $('.top_slider_blog_item').find('.feets').each(function(n, image) {
        var image = $(image);
        image.attr({
            style: image.attr('style').replace("s72", "s1600")
        });
        image.attr({
            style: image.attr('style').replace("s320", "s1600")
        });
        image.attr({
            style: image.attr('style').replace("s400", "s1600")
        });
        image.attr({
            style: image.attr('style').replace("s640", "s1600")
        })
    })
});
