$(document).ready(function() {

  //CODE OPTIONS

  var maxSearched = 500;
  var minimum = 3;
  var maximum = 3;
  var imageSize = 250;
  var roundImages = false;
  var relatedTitle = true;
  var labelInTitle = true;
  var relatedTitleText = "more posts about";
  var defaultImage = "";
  var campaignTracking = false;
  var campaignSource = "";
  var campaignMedium = "";
  var campaignName = "";
  var postLabels = $('.post-labels a');
  var insertAfter = $('.author-info');


  //END CODE OPTIONS - DO NOT EDIT BELOW THIS LINE

var labels = [];
var label;
var alternateLabel;
var minposts = minimum - 1;
if (postLabels.length === 1) {
    label = postLabels.text().trim()
} else if (postLabels.length > 1) {
    $(postLabels).each(function() {
        labels.push($(this).text().trim())
    });
    label = labels[Math.floor(labels.length * Math.random())];
    var labelLocation = labels.indexOf(label);
    if (labelLocation === labels.length - 1) {
        alternateLabel = labels[labelLocation - 1]
    } else {
        alternateLabel = labels[labelLocation + 1]
    }
}
if (label !== undefined) {
    var title = $(".entry-title").text().trim();
    var labelCount = 0;
    var alternateLabelCount = 0;
    var matches = [];
    var url = $('link[rel="alternate"][title*="Atom"]').eq(0).attr("href");
    $.ajax({
        url: url,
        data: {
            "max-results": maxSearched,
            alt: "json-in-script"
        },
        dataType: "jsonp",
        success: function(e) {
            $.each(e.feed.entry, function(t, n) {
                if (e.feed.entry[t].category !== undefined) {
                    var r = [];
                    for (var i = 0; i < e.feed.entry[t].category.length; i++) {
                        r.push(e.feed.entry[t].category[i].term)
                    }
                    if ($.inArray(label, r) !== -1) {
                        labelCount++
                    }
                    if ($.inArray(alternateLabel, r) !== -1) {
                        alternateLabelCount++
                    }
                }
            });
            if (labelCount <= minposts && alternateLabelCount >= minposts) {
                label = alternateLabel
            }
            $.each(e.feed.entry, function(t, n) {
                if (e.feed.entry[t].category !== undefined) {
                    var r = [];
                    for (var i = 0; i < e.feed.entry[t].category.length; i++) {
                        r.push(e.feed.entry[t].category[i].term)
                    }
                    if ($.inArray(label, r) !== -1) {
                        var s = e.feed.entry[t].title.$t.trim();
                        if (s !== title) {
                            var o;
                            for (var u = 0; u < e.feed.entry[t].link.length; u++) {
                                if (e.feed.entry[t].link[u].rel === "alternate") {
                                    o = e.feed.entry[t].link[u].href
                                }
                            }
                            var a;
                            if (e.feed.entry[t].media$thumbnail !== undefined) {
                                a = e.feed.entry[t].media$thumbnail.url.split(/s72-c/).join("s" + imageSize + "-c")
                            } else {
                                a = defaultImage
                            }
                            if (matches.length < maxSearched && campaignTracking === false) {
                                matches.push('<li class="related-posts-item"><a href="' + o + '"><img class ="related-posts-image" src="' + a + '" alt="' + s + '" nopin="nopin">' + '<span class="related-post-title">' + s + '</span>' + "</a></li>")
                            } else if (matches.length < maxSearched && campaignTracking === true) {
                                matches.push('<li class="related-posts-item"><a href="' + o + "?utm_source=" + campaignSource + "&utm_medium=" + campaignMedium + "&utm_campaign=" + campaignName + '"><img class ="related-posts-image" src="' + a + '" alt="' + s + '" nopin="nopin">' + '<span class="related-post-title">' + s + '</span>' + "</a></li>")
                            }
                        }
                    }
                }
            });
            if (matches.length >= minimum) {
                if (relatedTitle === true && labelInTitle === true) {
                    insertAfter.after('<div class="related-posts"><h5 class="related-posts-heading">Posts you may like' + "</h5><ul class='related-posts-wrapper clearfix'></ul></div>")
                } else if (relatedTitle === true && labelInTitle === false) {
                    insertAfter.after('<div class="related-posts"><h5 class="related-posts-heading">' + relatedTitleText + "</h5><ul class='related-posts-wrapper clearfix'></ul></div>")
                } else {
                    insertAfter.after('<div class="related-posts"><ul class="related-posts-wrapper clearfix"></ul></div>')
                }
                matches.sort(function() {
                    return .5 - Math.random()
                });
                for (var t = 0; t < maximum; t++) {
                    $(".related-posts .related-posts-wrapper").append(matches[t])
                }
            }
            insertAfter.css("clear", "both")
        }
    })
}
});


//

body.script:
//

  var tab1 = $("#sidebartab-item1");
  var tab2 = $("#sidebartab-item2");
  var tabWidget = document.querySelectorAll(".sidebartabs-wrapper .section .widget");
  var active = false;

  tab1.on("click", function () {
    if(active) {
      tab1.addClass("activetab"); 
	  tab2.removeClass("activetab");
      tabWidget[1].style.display = "none";
      tabWidget[0].style.display = "block"; 
      active = false;
    } 
  });

  tab2.on("click", function () {
    if(!active) {
      tab2.addClass("activetab");
      tab1.removeClass("activetab");
      tabWidget[0].style.display = "none";
      tabWidget[1].style.display = "block"; 
      active = true;
    } 
  });

  var bars = $(".top-nav-icon");
  var navMenu = $(".top-nav-wrapper .widget:first-child ul");

  bars.on("click", function () {
    navMenu.slideToggle();
  });


//
