$(document).ready(function() {
    $('.PopularPosts img').each(function() {
        $(this).attr('src', function(i, src) {
            return src.replace('s72-c', 's400-c')
        })
    });
    $('body.slider1 .slicktopgrid').slick({
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        dots: false,
        autoplay: true,
        autoplaySpeed: 4500,
        speed: 1200,
        cssEase: 'cubic-bezier(0.85, 0, 0.38, 1)',
        responsive: [{
            breakpoint: 900,
            settings: {
                slidesToShow: 1,
                slidesToScroll: 1,
                infinite: true,
                dots: false
            }
        }, {
            breakpoint: 600,
            settings: {
                slidesToShow: 1,
                slidesToScroll: 1
            }
        }, {
            breakpoint: 480,
            settings: {
                slidesToShow: 1,
                slidesToScroll: 1
            }
        }]
    });
    $('body.slider2 .slicktopgrid').slick({
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        dots: false,
        autoplay: true,
        autoplaySpeed: 4500,
        speed: 1200,
        cssEase: 'cubic-bezier(0.85, 0, 0.38, 1)',
        prevArrow: $('.custom-prev-slide'),
        nextArrow: $('.custom-next-slide'),
        responsive: [{
            breakpoint: 900,
            settings: {
                slidesToShow: 1,
                slidesToScroll: 1,
                infinite: true,
                dots: false
            }
        }, {
            breakpoint: 600,
            settings: {
                slidesToShow: 1,
                slidesToScroll: 1
            }
        }, {
            breakpoint: 480,
            settings: {
                slidesToShow: 1,
                slidesToScroll: 1
            }
        }]
    });
    $('span.sb-icon-search').insertAfter('#megamenuid>li:last-child');
    var ico = $('<i class="fa fa-caret-down"></i>');
    var ico2 = $('<i class="fa fa-caret-down"></i>');
    var ico1 = $('<i class="fa fa-caret-right"></i>');
    $('#megamenuid > li:has(ul) > a').append(ico);
    $('#megamenuid li:has(ul)  li:has(ul)>a').append(ico1);
    $(".sb-icon-search").click(function() {
        $('div#sb-search').addClass("showsearch");
        $('.sb-icon-close').addClass("newshow")
    });
    $(".sb-icon-close").click(function() {
        $('div#sb-search').removeClass("showsearch");
        $('.sb-icon-close').removeClass("newshow")
    });
    $('.sidebutton').click(function() {
        $('#sidebar-wrapper').toggleClass('alistarbot');
        $('.main-wrapper').toggleClass('sidebody')
    })
});
if ($('#sidebar-wrapper.alistarbot')) {
    $('.main-wrapper').click(function() {
        $('#sidebar-wrapper').removeClass('alistarbot');
        $('.main-wrapper').removeClass('sidebody')
    })
}
