function stripTags(s, n) {
    return s.replace(/<.*?>/ig, "").split(/\s+/).slice(0, n - 1).join(" ")
}


function rm(a, yo) {
    var p = document.getElementById(a);
    imgtag = "";
    ifrtag = "";
    ifrsrc = "";
    ifrtb = -1;
    img = p.getElementsByTagName("img");
    ifr = p.getElementsByTagName("iframe");
    for (var i = 0; i < ifr.length; i++) {
        ifrsrc = ifr[i].src;
        if (ifrsrc.indexOf("//www.youtube.com/embed/") != -1) {
            ifrtb = i;
            break
        } else if (ifrsrc.indexOf("//player.vimeo.com/video/") != -1) {
            ifrtb = i;
            break
        } else if (ifrsrc.indexOf("//www.dailymotion.com/embed/video/") != -1) {
            ifrtb = i;
            break
        }
    }
    if (ifrtb != -1) {
        ifrtag = '<div class="entry-video"><iframe  id="iframe1" src="' + ifrsrc + '?vq=medium&rel=0" frameborder="0" allowfullscreen></iframe></div>';
        p.innerHTML = ifrtag + '<span class="label2">' + yo + '</span><div class="entry-yotem"><header class="page-header1"><span class="author">By <a href="' + y + '">' + z + '</a> </span><h2 class="page-title"><a href="' + y + '">' + x + '</a></h2><ul class="entry-meta"><li class="comments-link"><a href="' + y + '#comments">' + u + ' Comments</a> </li></ul><div class="entry-content cf">' + stripTags(p.innerHTML, 45) + '<br/><span class="publish-date">Posted on ' + t + '</span><span class="fixed-char"><a class="read-more" href="' + y + '">Continue Reading...</a></span></div>'
    } else if (img.length >= 1) {
        imgtag = '<div class="post-image" id="image1"><a href="' + y + '"><img width="580" height="360"  class="thumb" src="' + img[0].src + '" /></a></div>';
        p.innerHTML = imgtag + '<span class="label2">' + yo + '</span><div class="entry-blog"><header class="page-header1"><span class="author">By <a href="' + y + '">' + z + '</a> </span><h2 class="page-title"><a href="' + y + '">' + x + '</a></h2><ul class="entry-meta"><li class="comments-link"><a href="' + y + '#comments">' + u + ' Comments</a> </li></ul><div class="entry-content cf">' + stripTags(p.innerHTML, 45) + '<br/><span class="publish-date">Posted on ' + t + '</span><span class="fixed-char"><a class="read-more" href="' + y + '">Continue Reading...</a></span></div>'
    } else if (img.length < 1) {
        imgtag = '</br>';
        p.innerHTML = imgtag + '<span class="label2">' + yo + '</span><div class="entry-blog"><header class="page-header1"><span class="author">By <a href="' + y + '">' + z + '</a> </span><h2 class="page-title"><a href="' + y + '">' + x + '</a></h2><ul class="entry-meta"><li class="comments-link"><a href="' + y + '#comments">' + u + ' Comments</a> </li></ul><div class="entry-content cf">' + stripTags(p.innerHTML, 45) + '<br/><span class="publish-date">Posted on ' + t + '</span><span class="fixed-char"><a class="read-more" href="' + y + '">Continue Reading...</a></span></div>'
    } else imgtag = ''
}



