var relatedTitles = new Array();
var relatedTitlesNum = 0;
var relatedUrls = new Array();
var thumburl = new Array();

function related_results_labels_thumbs(e) {
    for (var i = 0; i < e.feed.entry.length; i++) {
        var f = e.feed.entry[i];
        relatedTitles[relatedTitlesNum] = f.title.$t;
        try {
            thumburl[relatedTitlesNum] = f.media$thumbnail.url
        } catch (error) {
            s = f.content.$t;
            a = s.indexOf("<img");
            b = s.indexOf("src=\"", a);
            c = s.indexOf("\"", b + 5);
            d = s.substr(b + 5, c - b - 5);
            if ((a != -1) && (b != -1) && (c != -1) && (d != "")) {
                thumburl[relatedTitlesNum] = d
            } else {
                if (typeof("http://3.bp.blogspot.com/-PpjfsStySz0/UF91FE7rxfI/AAAAAAAACl8/092MmUHSFQ0/s1600/no_image.jpg") !== 'undefined') thumburl[relatedTitlesNum] = "http://3.bp.blogspot.com/-PpjfsStySz0/UF91FE7rxfI/AAAAAAAACl8/092MmUHSFQ0/s1600/no_image.jpg";
                else thumburl[relatedTitlesNum] = "http://3.bp.blogspot.com/-PpjfsStySz0/UF91FE7rxfI/AAAAAAAACl8/092MmUHSFQ0/s1600/no_image.jpg"
            }
        }
        if (relatedTitles[relatedTitlesNum].length > 35) relatedTitles[relatedTitlesNum] = relatedTitles[relatedTitlesNum].substring(0, 35) + "...";
        for (var k = 0; k < f.link.length; k++) {
            if (f.link[k].rel == 'alternate') {
                relatedUrls[relatedTitlesNum] = f.link[k].href;
                relatedTitlesNum++
            }
        }
    }
}

function removeRelatedDuplicates_thumbs() {
    var a = new Array(0);
    var b = new Array(0);
    var c = new Array(0);
    for (var i = 0; i < relatedUrls.length; i++) {
        if (!contains_thumbs(a, relatedUrls[i])) {
            a.length += 1;
            a[a.length - 1] = relatedUrls[i];
            b.length += 1;
            c.length += 1;
            b[b.length - 1] = relatedTitles[i];
            c[c.length - 1] = thumburl[i]
        }
    }
    relatedTitles = b;
    relatedUrls = a;
    thumburl = c
}

function contains_thumbs(a, e) {
    for (var j = 0; j < a.length; j++)
        if (a[j] == e) return true;
    return false
}

function printRelatedLabels_thumbs(a) {
    var b;
    if (typeof("#DDDDDD") !== 'undefined') b = "#DDDDDD";
    else b = "#DDDDDD";
    for (var i = 0; i < relatedUrls.length; i++) {
        if ((relatedUrls[i] == a) || (!relatedTitles[i])) {
            relatedUrls.splice(i, 1);
            relatedTitles.splice(i, 1);
            thumburl.splice(i, 1);
            i--
        }
    }
    var r = Math.floor((relatedTitles.length - 1) * Math.random());
    var i = 0;
    if (relatedTitles.length > 0) document.write();
    document.write('<div style="clear: both;"/>');
    while (i < relatedTitles.length && i < 20 && i < 5) {
        document.write('<a style="text-decoration:none;padding:5px;float:left;');
        if (i != 0) document.write('border-left:solid 0.5px ' + b + ';"');
        else document.write('"');
        document.write(' href="' + relatedUrls[r] + '"><img style="width:127px;height:127px;border:0px;" src="' + thumburl[r] + '"/><br/><div style="width:126px;padding-left:3px;border: 0pt none ; margin: 3px 0pt 0pt; padding: 0pt; font-style: normal; font-variant: normal; font-weight: normal; font-size: 15px; line-height: normal; font-size-adjust: none; font-stretch: normal;">' + relatedTitles[r] + '</div></a>');
        i++;
        if (r < relatedTitles.length - 1) {
            r++
        } else {
            r = 0
        }
    }
    document.write('</div>');
    relatedUrls.splice(0, relatedUrls.length);
    thumburl.splice(0, thumburl.length);
    relatedTitles.splice(0, relatedTitles.length)
}
