function nav_rundown(){
	var window_height = $(window).height();

	$(window).on('debouncedresize',function(e){
	    if( window_height != $(window).height() ){
			window_height = $(window).height();
		};
	});

	$('.close-navigation-button').click(function(e){
		e.preventDefault();

		$('#nav-hook, .mini-navigation').removeClass('is-closed').addClass('is-opened');
		$('.navigation-wrap, .large-navigation, .close-navigation-button').removeClass('is-opened').addClass('is-closed'); 

		$('.close-navigation-button').toggleClass('is-mobile');


		$('.navigation-list').toggleClass('is-opened');

		$('#nav-hook, .navigation-wrap').toggleClass('is-mobile');

	});



	$('.open-navigation').click(function(e){
		e.preventDefault();
		$('html').css('height', window_height+'px');

		$('#nav-hook, .mini-navigation').removeClass('is-opened').addClass('is-closed');
		$('.navigation-wrap, .large-navigation, .close-navigation-button').removeClass('is-closed').addClass('is-opened');  
	});

};


jQuery( document ).ready(function( $ ) {
	nav_rundown();
	$('.navigation-list > li:has(ul)').addClass('has-dropdown');
	$('.nav-accordion').dcAccordion();

