function showgalleryposts(e) {
    var t = e.feed.openSearch$totalResults.$t;
    var n = new Array;
    document.write("<div class='more_posts clearfix'>");
    for (var r = 0; r < t; ++r) {
        n[r] = r
    }
    if (random_posts == true) {
        n.sort(function() {
            return .5 - Math.random()
        })
    }
    if (numposts_gal > t) {
        numposts_gal = t
    }
    for (r = 0; r < numposts_gal; ++r) {
        var i = e.feed.entry[n[r]];
        var o = i.title.$t;
        for (var u = 0; u < i.link.length; u++) {
            if (i.link[u].rel == "alternate") {
                posturl_gal = i.link[u].href;
                posturl_gal = i.link[u].href;
                posturl_gal = i.link[u].href;
                break
            }
        }
        if ("content" in i) {
            var f = i.content.$t
        }
        s = f;
        a = s.indexOf("<img");
        b = s.indexOf('src="', a);
        c = s.indexOf('"', b + 5);
        d = s.substr(b + 5, c - b - 5);
        if (a != -1 && b != -1 && c != -1 && d != "") {
            var l = d
        } else var l = "http://4.bp.blogspot.com/-7i5f7PGr6jg/U1Vxas8xDzI/AAAAAAAABHM/6cPFGkDqi6U/s1600/Images_no_image.gif";
        var h = e.feed.entry[n[r]];
        var p = h.published.$t;
        var v = p.substring(0, 4);
        var m = p.substring(5, 7);
        var g = p.substring(8, 10);
        var y = new Array;
        y[1] = "Jan";
        y[2] = "Feb";
        y[3] = "Mar";
        y[4] = "Apr";
        y[5] = "May";
        y[6] = "Jun";
        y[7] = "Jul";
        y[8] = "Aug";
        y[9] = "Sep";
        y[10] = "Oct";
        y[11] = "Nov";
        y[12] = "Dec";
        var w = "";
        var E = 0;
        if (shdater == true) {
            w = w + y[parseInt(m, 10)] + " " + g + ", " + v;
            E = 1
        }
        document.write('<div class="item_small">');
        document.write('<div class="featured_thumb"><a href="' + posturl_gal + '" title="' + o + '"><img src="' + l.replace("/s1600/", "/s85-c/") + '" class="attachment-thumbnail wp-post-image" alt="' + o + '"/></a></div><div class="item-details"><h3><a href="' + posturl_gal + '" rel="bookmark" title="' + posturl_gal + '">' + o + '</a></h3><div class="post_meta"><a href="' + posturl_gal + '"><i class="fa fa-calendar-o mi"></i>' + w + "</a></div></div>");
        document.write("</div>")
    }
    document.write("</div>")
}


