function stripTags(s, n) {
    return s.replace(/<.*?>/ig, "").split(/\s+/).slice(0, n - 1).join(" ")
}

function readmore(a) {
    var p = document.getElementById(a),
        imgtag = "",
        img = p.getElementsByTagName("img");
    if (img.length >= 1) imgtag = '<img class="thumb" src="' + img[0].src + '" />';
    else imgtag = '<img class="thumb no-img" src="https://lh4.googleusercontent.com/-G9M2DTCTUwM/Tlh-2pwtc5I/AAAAAAAABKM/kCJg-Kf3W2M/no_image_yet.jpg" />';
    p.innerHTML = '<a href="' + y + '"><div class="large_thumb thumb_hover"><div class="img_wrapper">' + imgtag + "</div></div></a>" + '<h3 class="post_item_title"><a rel="bookmark" href="' + y + '">' + x + "</a></h3>" + '<div class="post_meta"><span class="user">by <span style="color: #E74C3C;">' + z + '</span></span> <span class="time">' + t + "</span></div>" + '<div class="post_item_inner"><p>' + stripTags(p.innerHTML, 20) + '...</p><a class="button normal" rel="bookmark" href="' + y + '">Read More</a><span class="like_post"><iframe class="fblike" allowTransparency="true" frameborder="0" scrolling="no" src="http://www.facebook.com/plugins/like.php?href=' + y + '&send=false&layout=button_count&show_faces=false&width=79&action=like&colorscheme=light"></iframe></span><div class="clear"></div></div>'
};
