  $(function () {

    if(numOfSliderPosts > 4) {
      var slider = $("#slider");

      function autoPlaySlider () {
        sliderAutoPlay = setInterval (function (){
          var ul = $(".slider-small-box");
          var marginTop = ul.css("margin-top");
          var ulHeight = ul.height();

          if(ulHeight + parseInt(marginTop, 10) <= 555) {
            ul.css("marginTop", "0px");
          } else {
            var s = parseInt(marginTop, 10);
            s = s - 185;
            ul.css("marginTop", s + "px");
          }
        }, 3000);
      };
      autoPlaySlider();

      slider.on("mouseenter", ".slider-small-container", function () {
        clearInterval(sliderAutoPlay);
      });

      slider.on("mouseleave", ".slider-small-container", autoPlaySlider);

      slider.on("click", "#up", function () {
        var ul = $(".slider-small-box");
        var marginTop = ul.css("margin-top");
        var ulHeight = ul.height();
        var f = parseInt(marginTop, 10);

        if((f%185 === 0) || (f%185 === -0)) { 
          if(ulHeight + parseInt(marginTop, 10) <= 555) {
            ul.css("marginTop", "0px");
          } else {
            var s = parseInt(marginTop, 10);
            s = s - 185;
            ul.css("marginTop", s + "px");
          }
        }
      });

      slider.on("click", "#down", function () {
        var ul = $(".slider-small-box");
        var marginTop = ul.css("margin-top");
        var ulHeight = ul.height();
        var f = parseInt(marginTop, 10);

        if((f%185 === 0) || (f%185 === -0)) { 
          if(f >= 0) {
            var a = (ul.children("li").length-3) * (-185);
            ul.css("marginTop", a + "px");
          } else {
            var s = parseInt(marginTop, 10);
            s = s + 185;
            ul.css("marginTop", s + "px");
          }
        }
      });
    }
  });

