  var v = $("#nav-items").find("ul");
  var f = false;

  v.on("click", function () {
    if(v.height() > 0) {
      if(!f) {
        v.css("height", "auto");
        f = true;
      } else {
        v.css("height", "40px");
        f = false;
      }
    }
  });

