function bp_thumbnail_resize(image_url, post_title) {
    image_tag = '<img src="' + image_url.replace('/s72-c/', '/s600/') + '" alt="' + post_title.replace(/"/g, "") + '" title="' + post_title.replace(/"/g, "") + '"/>';
    if (post_title != "") return image_tag;
    else return ""
}
