function stripTags(s, n) {
    return s.replace(/<.*?>/ig, "").split(/\s+/).slice(0, n - 1).join(" ")
}

function readmore(a) {
    var p = document.getElementById(a),
        imgtag = "",
        ifrtag = "";
    ifrsrc = "";
    ifrtb = -1;
    img = p.getElementsByTagName("img");
    ifr = p.getElementsByTagName("iframe");
    for (var i = 0; i < ifr.length; i++) {
        ifrsrc = ifr[i].src;
        if (ifrsrc.indexOf("//www.youtube.com/embed/") != -1) {
            ifrtb = i;
            break
        } else if (ifrsrc.indexOf("//player.vimeo.com/video/") != -1) {
            ifrtb = i;
            break
        } else if (ifrsrc.indexOf("//www.dailymotion.com/embed/video/") != -1) {
            ifrtb = i;
            break
        } else if (ifrsrc.indexOf("//w.soundcloud.com/player/") != -1) {
            break
        }
    }
    if (ifrtb != -1) ifrtag = '<div class="entry-video"><iframe width="500" height="250" src="' + ifrsrc + '?vq=medium&rel=0" frameborder="0" allowfullscreen></iframe></div>';
    else if (img.length >= 1) imgtag = '<div class="entry-image"><a href="' + y + '"><img class="thumb" src="' + img[0].src + '" /></a></div>';
    else imgtag = '<div class="entry-image no-image"><a href="' + y + '"><img class="thumb" src="https://lh4.googleusercontent.com/-G9M2DTCTUwM/Tlh-2pwtc5I/AAAAAAAABKM/kCJg-Kf3W2M/no_image_yet.jpg" /></a></div>';
    p.innerHTML = '<a href="' + y + '"><div class="large_thumb thumb_hover"><div class="img_wrapper">' + ifrtag + imgtag + "</div></div></a>" + '<div class="foundation"> <div class="post-avatar">' + o + '</div><div class="avatar-name"><span class="user">By <span style="color: #999;">' + z + '</span></span></div> </div><h3 class="post_item_title"><a rel="bookmark" href="' + y + '">' + x + "</a></h3>" + '<div class="post_meta">Date : <span class="time0">' + t + "</span></div>" + '<div class="post_item_inner"><p>' + stripTags(p.innerHTML, 20) + '...</p><a class="button normal" rel="bookmark" href="' + y + '">Read More</a></span><div class="clear"></div></div>'
};



