        $(Cur_Cform_Hdr)
            .html(reset_html)
    } else {
        $(Cur_Cform_Hdr)
            .html('')
    }
    Cur_Cform_Hdr = '#r_f_c' + par_id;
    $(Cur_Cform_Hdr)
        .html(html);
    $('#comment-editor')
        .attr('src', Cform_Ins_ParID(par_id))
}
