function toggleTitleSort() {
    if (sortBy == "titleasc") {
        sortBy = "titledesc"
    } else {
        sortBy = "titleasc"
    };
    sortPosts(sortBy);
    displayToc(postFilter)
};

function toggleDateSort() {
    if (sortBy == "datenewest") {
        sortBy = "dateoldest"
    } else {
        sortBy = "datenewest"
    };
    sortPosts(sortBy);
    displayToc(postFilter)
};

