function ShowPost1(a) {
    (function(b) {
        var g = {
            blogURL: "",
            MaxPost: 7,
            idcontaint: "",
            FirstImageSize: 360,
            ImageSize: 240,
            Summarylength: 185,
            animated: false,
            loadingClass: "loadingz",
            pBlank: "",
            MonthNames: ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"],
            tagName: false
        };
        g = b.extend({}, g, a);
        var d = b(g.idcontaint);
        d.html('<div class="buo-ngjowo"><ul class="uong-widget"></ul></div>').addClass(g.loadingClass);
        b.get((g.blogURL === "" ? window.location.protocol + "//" + window.location.host : g.blogURL) + "/feeds/posts/summary" + (g.tagName === false ? "" : "/-/" + g.tagName) + "?max-results=" + g.MaxPost + "&orderby=published&alt=json-in-script", function(t) {
            var m, u, h, M, o, v, k, s, r, l, q, j, f, w = "",
                p = t.feed.entry;
            for (var n = 0; n < p.length; n++) {
                for (var e = 0; e < p[n].link.length; e++) {
                    if (p[n].link[e].rel == "alternate") {
                        m = p[n].link[e].href;
                        break
                    }
                }
                for (var f = 0; f < e; f++) {
                    if (p[n].link[f].rel == "replies" && p[n].link[f].type == "text/html") {
                        M = p[n].link[f].title.split(" ")[0];
                        break
                    }
                }
                if ("media$thumbnail" in p[n]) {
                    o = p[n].media$thumbnail.url
                } else {
                    o = g.pBlank
                }
                if ("content" in p[n]) {
                    h = p[n].content.$t
                } else {
                    if ("summary" in p[n]) {
                        h = p[n].summary.$t
                    } else {
                        h = ""
                    }
                }
                h = h.replace(/<\S[^>]*>/g, "");
                if (h.length > g.Summarylength) {
                    h = h.substring(0, g.Summarylength) + "..."
                }
                if (p[n] === p[0]) {
                    o = o.replace(/\/s[0-9]+\-c/g, "/s" + g.FirstImageSize + "-p");
                    if (o.indexOf("img.youtube.com/vi/") != -1) {
                        o = o.replace("default", "0")
                    }
                    k = "<p>" + h + "</p>"
                } else {
                    o = o.replace(/\/s[0-9]+\-c/g, "/s" + g.ImageSize + "-p");
                    k = ""
                }
                u = p[n].title.$t;
                f = p[n].published.$t.substring(0, 10);
                s = f.substring(0, 4);
                r = f.substring(5, 7);
                l = f.substring(8, 10);
                q = g.MonthNames[parseInt(r, 10) - 1];
                v = '<div class="cabbo"><a title="' + u + '" class=" " href="' + m + '"><img src="' + o + '"/></a></div>';
                j = '<div class="mores"><a class="more-info" href="' + m + '">View More</a></div>';
                w += '<li><div class="dingo">' + v + ' <h3><a href="' + m + '">' + u + '</a></h3><div class="bean"><div class="meta-info">' + l + ' ' + q + ' ' + s + ' - ' + M + ' comment</div>' + k + ' ' + j + '<div class="clear"></div></div></div></li>'
            }
            b("ul", d).append(w);
            if (g.animated) {
                b("ul", d).addClass("scroll-item");
                b(g.idcontaint + " .buo-ngjowo").flexslider({
                    animation: "slide",
                    selector: ".scroll-item > li",
                    animationLoop: true,
                    itemWidth: 290,
                    pauseOnHover: true,
                    move: 3,
                    controlNav: true,
                    itemMargin: 0,
                    minItems: 4,
                    mousewheel: false,
                    maxItems: 4
                })
            } else {
                if (g.tagName != false) {
                    d.append('<h4 class="uj-box-title"><a href="/search/label/' + encodeURIComponent(g.tagName) + '?&max-results=7"><i class="icon-list-ul"></i></a></h4>')
                }
            }
            d.removeClass(g.loadingClass)
        }, "jsonp")
    })(jQuery)
}

