var j = jQuery.noConflict();

function initAccToc() {
    j("#tuzer .caster").hide();
    j("#tuzer  .fabe").css("cursor", "pointer");
    j("#tuzer .caster:first").show();
    j("#tuzer .fabe:first").addClass("headactive");
    j("#tuzer .fabe").click(function() {
        if (accToc) {
            var a = j(this).next();
            if ((a.is(".caster")) && (a.is(":visible"))) {
                j(this).next().slideToggle("normal");
                j(this).toggleClass("headactive");
                return false
            }
            if ((a.is(".caster")) && (!a.is(":visible"))) {
                j("#tuzer  .caster:visible").slideUp("normal");
                j("#tuzer  .fabe").removeClass("headactive");
                a.slideDown("normal");
                j(this).addClass("headactive");
                return false
            }
        } else {
            j(this).next().slideToggle("normal");
            j(this).toggleClass("headactive")
        }
    })
}
j(document).ready(function() {
    initAccToc()
})
