b:if.script:
function insertAfter(addition,target) {
var parent = target.parentNode;
if (parent.lastChild == target) {
parent.appendChild(addition); 
} else {
parent.insertBefore(addition,target.nextSibling);
}
}
var adscont = document.getElementById("adsense-content");
var target = document.getElementById("adsense-target");
var linebreak = target.getElementsByTagName("br");
if (linebreak.length > 0){
insertAfter(adscont,linebreak[0]);
}

/**/
//Automatic Read More
function removeHtmlTag(strx, chop) {
	if (strx.indexOf("<") != -1) {
		var s = strx.split("<");
		for (var i = 0; i < s.length; i++) {
			if (s[i].indexOf(">") != -1) {
				s[i] = s[i].substring(s[i].indexOf(">") + 1, s[i].length)
			}
		}
		strx = s.join("")
	}
	chop = (chop < strx.length - 1) ? chop : strx.length - 2;
	while (strx.charAt(chop - 1) != ' ' && strx.indexOf(' ', chop) != -1) chop++;
	strx = strx.substring(0, chop - 1);
	return strx + '...'
}

function btntSummaryAndThumb(btnt1, btnt2, btnt3) {
	var div = document.getElementById(btnt1);
	var imgtag = "";
	var img = div.getElementsByTagName("img");
	var summ = 700;
	if (img.length >= 1) {
		imgtag = '<div class="post_thumbnail"><a href="' + btnt3 + '"><img src="' + img[0].src + '"/><div class="thumb-hover"></div></a></div><div class="btnt-post">';
		summ = 240
	} else if (img.length >= 0) {
		imgtag = '<div class="post_thumbnail"><a href="' + btnt3 + '"><img src="http://4.bp.blogspot.com/-7pWJpJ09ct0/VnpUJoqNvKI/AAAAAAAAANw/2KR6NOt6GnE/s400/captain_jack_sparrow___vector.png"/><div class="thumb-hover"></div></a></div><div class="btnt-post">'
	}
	var summary = imgtag + '<div class="entry-sum">' + removeHtmlTag(div.innerHTML, summ) + '</div></div>';
	div.innerHTML = summary
}
