  /**/
    //Featured Post With Thumbnail Widget Script    
    var rnumposts = 5;
    var rdisplaymore = true;
    var rshowcommentnum = true;
    var rshowpostdate = true;
    var rshowpostsummary = true;
    var rnumchars = 100;

    imgr = new Array();
    imgr[0] = "http://i1203.photobucket.com/albums/bb382/chandeep10/thumb_missing.png";
    showRandomImg = true;
function showfeaturedpostswiththumbs(json) {
  document.write('<ul class="recent_posts_with_thumbs">');
  j = (showRandomImg) ? Math.floor((imgr.length + 1) * Math.random()) : 0;
  img = new Array();
  for(var i = 0; i < rnumposts; i++) {
    var entry = json.feed.entry[i];
    var posttitle = entry.title.$t;
    var posturl;
    if(i == json.feed.entry.length) break;
    for(var k = 0; k < entry.link.length; k++) {
      if(entry.link[k].rel == 'replies' && entry.link[k].type == 'text/html') {
        var commenttext = entry.link[k].title;
        var commenturl = entry.link[k].href
      }
      if(entry.link[k].rel == 'alternate') {
        posturl = entry.link[k].href;
        break
      }
    }
    if("content" in entry) {
      var postcontent = entry.content.$t
    } else if("summary" in entry) {
      var postcontent = entry.summary.$t
    } else var postcontent = "";
    if(j > imgr.length - 1) j = 0;
    img[i] = imgr[j];
    s = postcontent;
    a = s.indexOf("<img");
    b = s.indexOf("src=\"", a);
    c = s.indexOf("\"", b + 5);
    d = s.substr(b + 5, c - b - 5);
    if((a != -1) && (b != -1) && (c != -1) && (d != "")) img[i] = d;
    var postdate = entry.published.$t;
    var cdyear = postdate.substring(0, 4);
    var cdmonth = postdate.substring(5, 7);
    var cdday = postdate.substring(8, 10);
    var monthnames = new Array();
    monthnames[1] = "Jan";
    monthnames[2] = "Feb";
    monthnames[3] = "Mar";
    monthnames[4] = "Apr";
    monthnames[5] = "May";
    monthnames[6] = "Jun";
    monthnames[7] = "Jul";
    monthnames[8] = "Aug";
    monthnames[9] = "Sep";
    monthnames[10] = "Oct";
    monthnames[11] = "Nov";
    monthnames[12] = "Dec";
    document.write('<li>');
    document.write('<div class="btnt-img-cont"><a class="recent_thumb_container" href="' + posturl + '"><img class="recent_thumb" src="' + img[i] +
      '"/><div class="thumb-hover"></div></a></div>');
    document.write('<div class="btnt-sum"><a class="post-title" href="' + posturl + '" target ="_top">' + posttitle + '</a>');
    var re = /<\S[^>]*>/g;
    postcontent = postcontent.replace(re, "");
    if(rshowpostsummary == true) {
      if(postcontent.length < rnumchars) {
        document.write('<span>');
        document.write(postcontent);
        document.write('</span>')
      } else {
        document.write('<span>');
        postcontent = postcontent.substring(0, rnumchars);
        var quoteEnd = postcontent.lastIndexOf(" ");
        postcontent = postcontent.substring(0, quoteEnd);
        document.write(postcontent + '...');
        document.write('</span>')
      }
    }
    var towrite = '';
    var flag = 0;
    document.write('<strong>');
    if(rshowpostdate == true) {
      towrite = towrite + monthnames[parseInt(cdmonth, 10)] + '   ' + cdday + ', ' + cdyear;
      flag = 1
    }
    if(rshowcommentnum == true) {
      if(flag == 1) {
        towrite = towrite + ' · '
      }
      if(commenttext == '1 Comments') commenttext = '1 Comment';
      if(commenttext == '0 Comments') commenttext = 'No Comments';
      commenttext = '<a href="' + commenturl + '" target ="_top">' + commenttext + '</a>';
      towrite = towrite + commenttext;
      flag = 1
    }
    if(rdisplaymore == true) {
      if(flag == 1) towrite = towrite + ' · ';
      towrite = towrite + '<a href="' + posturl + '" class="url" target ="_top">More</a>';
      flag = 1
    }
    document.write(towrite);
    document.write('</strong></div></li>')
  }
  document.write('</ul>')
}



//Slider Script
var snumposts = 5;
var sdisplaymore = false;
var sshowpostsummary = true;
var snumchars = 150;

function showfeaturepostswiththumbs(json) {
  document.write('<ul class="rslides">');
  for(var i = 0; i < snumposts; i++) {
    var entry = json.feed.entry[i];
    var posttitle = entry.title.$t;
    var posturl;
    if(i == json.feed.entry.length) break;
    for(var k = 0; k < entry.link.length; k++) {
      if(entry.link[k].rel == 'alternate') {
        posturl = entry.link[k].href;
        break
      }
    }
    var thumburl;
    try {
      thumburl = entry.media$thumbnail.url
    } catch(error) {
      s = entry.content.$t;
      a = s.indexOf("<img");
      b = s.indexOf("src=\"", a);
      c = s.indexOf("\"", b + 5);
      d = s.substr(b + 5, c - b - 5);
      if((a != -1) && (b != -1) && (c != -1) && (d != "")) {
        thumburl = d
      } else thumburl = 'https://lh3.googleusercontent.com/_FQCCkMjyvSA/TWoCU9DxctI/AAAAAAAAAHk/ZW-sgbYYWBs/thumb_missing.jpg'
    }
    document.write('<li>');
    document.write('<a class="feature_thumb_container" href="' + posturl + '"><img class="feature_thumb" src="' + thumburl.replace('/s72-c/', '/s600/') +
      '"/><div class="thumb-hover"></div></a>');
    document.write('<div class="slide-caption"><div class="slide-caption-wrap">');
    document.write('<a class="post-title" href="' + posturl + '" target ="_top">' + posttitle + '</a>');
    if("content" in entry) {
      var postcontent = entry.content.$t
    } else if("summary" in entry) {
      var postcontent = entry.summary.$t
    } else var postcontent = "";
    var re = /<\S[^>]*>/g;
    postcontent = postcontent.replace(re, "");
    if(sshowpostsummary == true) {
      if(postcontent.length < snumchars) {
        document.write('<span>');
        document.write(postcontent);
        document.write('</span>')
      } else {
        document.write('<span>');
        postcontent = postcontent.substring(0, snumchars);
        var quoteEnd = postcontent.lastIndexOf(" ");
        postcontent = postcontent.substring(0, quoteEnd);
        document.write(postcontent + '...');
        document.write('</span>')
      }
    }
    var towrite = '';
    var flag = 0;
    document.write('<strong>');
    if(sdisplaymore == true) {
      if(flag == 1) towrite = towrite + ' · ';
      towrite = towrite + '<a href="' + posturl + '" class="url" target ="_top">Read the rest of this entry</a>';
      flag = 1
    }
    document.write(towrite);
    document.write('</strong></div></div>');
    document.write('</li>')
  }
  document.write('</ul>')
}



