  $(function(){
    var opened = false;
    var sbtn = $("#search-btn");

    $("#search-icon").on("click", function (){
      sbtn.toggleClass("white");
      var search = $(".search");
      if(!opened){
        search.css("top", "0");
        opened = true;
      } else {
        search.css("top", "-50px");
        opened = false;
      }
    });

    var top = $("#to-top");
    var scrollTop;
    if (top.length) {
      var scrollTrigger = 200, // px
          backToTop = function () {
            scrollTop = $(window).scrollTop();
            if (scrollTop > scrollTrigger) {
              top.addClass('fadeIn');
            } else {
              top.removeClass('fadeIn');
            }
          };
      backToTop();
      $(window).on('scroll', function () {
        backToTop();
      });
      top.on('click', function (e) {
        e.preventDefault();
        $('html,body').animate({
          scrollTop: 0
        }, 600);
      });
    }
  });

