  $(function (){
    var v = $("#main-nav").find("ul");
    var f = false;

    v.on("click", function () {
      if(v.height() > 0) {
        if(!f) {
          v.css("height", "auto");
          f = true;
        } else {
          v.css("height", "50px");
          f = false;
        }
      }
    });

    var bars = $(".top-nav-icon");
    var navMenu = $(".top-nav-wrapper .widget:first-child ul");

    bars.on("click", function () {
      navMenu.slideToggle();
    });
  });

