    var menu = $('.menu');
    $(touch).on('click', function(e) {
        e.preventDefault();
        menu.slideToggle();
    });
    $(window).resize(function() {
        var w = $(window).width();
        if (w > 800 && menu.is(':hidden')) {
            menu.removeAttr('style');
        }
    });
});
