function getPostUrl(t) {
    for (var e = 0; e < t.link.length; e++)
        if ("alternate" == t.link[e].rel) {
            var s = t.link[e].href;
            return s
        }
}

function getPostImg(t, e, s) {
    if ("content" in t) var i = t.content.$t;
    var n = i,
        r = n.indexOf("<img"),
        a = n.indexOf('src="', r),
        o = n.indexOf('"', a + 5),
        l = n.substr(a + 5, o - a - 5);
    if (-1 != r && -1 != a && -1 != o && "" != l) {
        e[s] = l;
        var d = e[s]
    } else var d = no_image_url;
    return d
}

function getPostPublishDate(t) {
    var e = t.published.$t,
        s = e.split("-")[2].substring(0, 2),
        i = e.split("-")[1],
        n = e.split("-")[0],
        r = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "Octobor", "November", "December"],
        a = r[i - 1],
        o = a + " " + s + ", " + n;
    return o
}

function getPostCategory(t) {
    var e = t.category;
    e && (e = t.category[0].term);
    var s = '<span class="category-wrapper">In: <a class="category" href="/search/label/' + e + '?max-results=10">' + e + "</a></span>";
    return e ? s : ""
}

function Slider(t) {
    for (var e = new Array, s = "", i = t.feed.entry.length, n = 0; i > n; n++) {
        var r = t.feed.entry[n],
            a = r.title.$t,
            o = getPostUrl(r),
            l = getPostImg(r, e, n),
            d = r.author[0].name.$t,
            c = getPostPublishDate(r),
            u = getPostCategory(r);
        s = s + '<div class="slider-post"><div class="post-image" style="background: url(&quot;' + l + '&quot;) no-repeat center;background-size: cover"></div><div class="post-info-wrapper"><div class="post-info"><span class="publish-date">' + c + '</span><a href="' + o + '" class="slider-post-title"><h2 class="heading">' + a + '</h2></a><div class="hr-line"></div>' + u + '<span class="writer">By: ' + d + '</span><a class="continue-read" href="' + o + '">continue reading</a></div></div></div>'
    }
    slider.html('<div class="slider-posts">' + s + "</div>"), windowWidth > 1200 ? $(".slider-posts").slick({
        infinite: !0,
