$(document).ready(function() {
    $("ul#sub-menu").parent("li").addClass("hasSub");
    $(".nav-menu ul li").each(function() {
        $(this).hoverTimeout(0, function() {
            $(this).children("ul").slideDown()
        }, 0, function() {
            $(this).children("ul").hide()
        })
    });
    $(".post-outer,.feat").each(function() {
        $(this).find(".block-image .thumb a,.primeiro .feat-thumb a").attr("style", function(e, t) {
            return t.replace("/default.jpg", "/mqdefault.jpg")
        }).attr("style", function(e, t) {
            return t.replace("s72-c", "s640")
        })
    })
});
$(document).ready(function() {
    $(".recent-boxes").appendTo(".gapad")
});
$(document).ready(function() {
    $('.ready-widget .HTML .widget-content').each(function() {
        var text = $(this).text();
        if (text.match('randomposts')) {
            $.ajax({
                url: "/feeds/posts/default?alt=json-in-script",
                type: 'get',
                dataType: "jsonp",
                success: function(datax) {
                    var numpost = datax.feed.entry.length;
                    var min = 0;
                    var max = numpost - randomposts_number;
                    var random = Math.floor(Math.random() * (max - min + 1)) + min;
                    $.ajax({
                        url: "/feeds/posts/default?alt=json-in-script&start-index=" + random + "&max-results=" + randomposts_number,
                        type: 'get',
                        dataType: "jsonp",
                        success: function(data) {
                            var posturl = "";
                            var htmlcode = '<ul class="roma-widget">';
                            for (var i = 0; i < data.feed.entry.length; i++) {
                                for (var j = 0; j < data.feed.entry[i].link.length; j++) {
                                    if (data.feed.entry[i].link[j].rel == "alternate") {
                                        posturl = data.feed.entry[i].link[j].href;
                                        break
                                    }
                                }
                                var posttitle = data.feed.entry[i].title.$t;
                                var postlabel = data.feed.entry[i].category[0].term;
                                var get_date = data.feed.entry[i].published.$t,
                                    year = get_date.substring(0, 4),
                                    month = get_date.substring(5, 7),
                                    day = get_date.substring(8, 10),
                                    date = text_month[parseInt(month, 10)] + ' ' + day + ', ' + year;
                                var tag = data.feed.entry[i].category[0].term;
                                var content = data.feed.entry[i].content.$t;
                                var $content = $('<div>').html(content);
                                if (content.indexOf("http://www.youtube.com/embed/") > -1 || content.indexOf("https://www.youtube.com/embed/") > -1) {
                                    var src2 = data.feed.entry[i].media$thumbnail.url;
                                    var thumb = '<a class="foot-mag-thumb" href="' + posturl + '" style="background:url(' + src2 + ') no-repeat center center;background-size: cover"/>'
                                } else if (content.indexOf("<img") > -1) {
                                    var src = $content.find('img:first').attr('src');
                                    var thumb = '<a class="foot-mag-thumb" href="' + posturl + '" style="background:url(' + src + ') no-repeat center center;background-size: cover"/>'
                                } else {
                                    var thumb = '<a class="foot-mag-thumb" href="' + posturl + '" style="background:url(' + no_image_url + ') no-repeat center center;background-size: cover"/>'
                                }
                                htmlcode += '<li><span class="p-tag">' + postlabel + '</span><div class="wid-thumb">' + thumb + '</div><div class="p-head"><h3 class="wrp-titulo"><a href="' + posturl + '">' + posttitle + '</a></h3><span class="p-date">' + date + '</span></div></li>'
                            }
                            htmlcode += '</ul><div class="clear"/>';
                            $('.ready-widget .HTML .widget-content').each(function() {
                                if ($(this).text().match('randomposts')) {
                                    $(this).html(htmlcode);
                                    $(this).parent().addClass('ideias-widget');
                                    $(this).find('.rcp-thumb').each(function() {
                                        $(this).attr('style', function(i, src) {
                                            return src.replace('/default.jpg', '/mqdefault.jpg')
                                        }).attr('style', function(i, src) {
                                            return src.replace('s72-c', 's1600')
                                        })
                                    });
                                    $("p.trans").each(function() {
                                        var e = $(this).text();
                                        var t = $(this).attr("data-tran");
                                        $("#pages-wrapper *").replaceText(e, t)
                                    })
                                }
                            })
                        }
                    })
                }
            })
        }
        if (text.match('recentposts')) {
            $.ajax({
                url: "/feeds/posts/default?alt=json-in-script",
                type: 'get',
                dataType: "jsonp",
                success: function(datax) {
                    $.ajax({
                        url: "/feeds/posts/default?alt=json-in-script&max-results=" + recentposts_number,
                        type: 'get',
                        dataType: "jsonp",
                        success: function(data) {
                            var posturl = "";
                            var htmlcode = '<ul class="roma-widget">';
                            for (var i = 0; i < data.feed.entry.length; i++) {
                                for (var j = 0; j < data.feed.entry[i].link.length; j++) {
                                    if (data.feed.entry[i].link[j].rel == "alternate") {
                                        posturl = data.feed.entry[i].link[j].href;
                                        break
                                    }
                                }
                                var posttitle = data.feed.entry[i].title.$t;
                                var postlabel = data.feed.entry[i].category[0].term;
                                var get_date = data.feed.entry[i].published.$t,
                                    year = get_date.substring(0, 4),
                                    month = get_date.substring(5, 7),
                                    day = get_date.substring(8, 10),
                                    date = text_month[parseInt(month, 10)] + ' ' + day + ', ' + year;
                                var tag = data.feed.entry[i].category[0].term;
                                var content = data.feed.entry[i].content.$t;
                                var $content = $('<div>').html(content);
                                if (content.indexOf("http://www.youtube.com/embed/") > -1 || content.indexOf("https://www.youtube.com/embed/") > -1) {
                                    var src2 = data.feed.entry[i].media$thumbnail.url;
                                    var thumb = '<a class="foot-mag-thumb" href="' + posturl + '" style="background:url(' + src2 + ') no-repeat center center;background-size: cover"/>'
                                } else if (content.indexOf("<img") > -1) {
                                    var src = $content.find('img:first').attr('src');
                                    var thumb = '<a class="foot-mag-thumb" href="' + posturl + '" style="background:url(' + src + ') no-repeat center center;background-size: cover"/>'
                                } else {
                                    var thumb = '<a class="foot-mag-thumb" href="' + posturl + '" style="background:url(' + no_image_url + ') no-repeat center center;background-size: cover"/>'
                                }
                                htmlcode += '<li><span class="p-tag">' + postlabel + '</span><div class="wid-thumb">' + thumb + '</div><div class="p-head"><h3 class="wrp-titulo"><a href="' + posturl + '">' + posttitle + '</a></h3><span class="p-date">' + date + '</span></div></li>'
                            }
                            htmlcode += '</ul><div class="clear"/>';
                            $('.ready-widget .HTML .widget-content').each(function() {
                                if ($(this).text().match('recentposts')) {
                                    $(this).html(htmlcode);
                                    $(this).parent().addClass('ideias-widget');
                                    $(this).find('.rcp-thumb').each(function() {
                                        $(this).attr('style', function(i, src) {
                                            return src.replace('/default.jpg', '/mqdefault.jpg')
                                        }).attr('style', function(i, src) {
                                            return src.replace('s72-c', 's1600')
                                        })
                                    });
                                    $("p.trans").each(function() {
                                        var e = $(this).text();
                                        var t = $(this).attr("data-tran");
                                        $("#pages-wrapper *").replaceText(e, t)
                                    })
                                }
                            })
                        }
                    })
                }
            })
        }
    })
});
$(document).ready(function() {
    $('.ready-widget .HTML .widget-content').each(function() {
        var t = $(this).html(),
            k = $(this).prev("h2").text();
        var z = t.match(/[^[\]]+(?=])/g);
        $(this).html("<span>" + z[0] + "</span><span>" + z[1] + "</span><span>" + z[2] + "</span>");
        var ideas = $(this).text();
        var g = $(this).find("span").eq(0).text();
        var style = $(this).find("span").eq(1).text();
        var l = $(this).find("span").eq(2).text();
        if (style.match('feat1')) {
            $.ajax({
                url: "/feeds/posts/default?alt=json-in-script",
                type: 'get',
                dataType: "jsonp",
                success: function(datax) {
                    $.ajax({
                        url: "/feeds/posts/default/-/" + g + "?alt=json-in-script&max-results=" + l,
                        type: 'get',
                        dataType: "jsonp",
                        success: function(data) {
                            var posturl = "";
                            var htmlcode = '<ul class="roma-widget">';
                            for (var i = 0; i < data.feed.entry.length; i++) {
                                for (var j = 0; j < data.feed.entry[i].link.length; j++) {
                                    if (data.feed.entry[i].link[j].rel == "alternate") {
                                        posturl = data.feed.entry[i].link[j].href;
                                        break
                                    }
                                }
                                var posttitle = data.feed.entry[i].title.$t;
                                var postlabel = data.feed.entry[i].category[0].term;
                                var get_date = data.feed.entry[i].published.$t,
                                    year = get_date.substring(0, 4),
                                    month = get_date.substring(5, 7),
                                    day = get_date.substring(8, 10),
                                    date = text_month[parseInt(month, 10)] + ' ' + day + ', ' + year;
                                var tag = data.feed.entry[i].category[0].term;
                                var content = data.feed.entry[i].content.$t;
                                var $content = $('<div>').html(content);
                                if (content.indexOf("http://www.youtube.com/embed/") > -1 || content.indexOf("https://www.youtube.com/embed/") > -1) {
                                    var src2 = data.feed.entry[i].media$thumbnail.url;
                                    var thumb = '<a class="foot-mag-thumb" href="' + posturl + '" style="background:url(' + src2 + ') no-repeat center center;background-size: cover"/>'
                                } else if (content.indexOf("<img") > -1) {
                                    var src = $content.find('img:first').attr('src');
                                    var thumb = '<a class="foot-mag-thumb" href="' + posturl + '" style="background:url(' + src + ') no-repeat center center;background-size: cover"/>'
                                } else {
                                    var thumb = '<a class="foot-mag-thumb" href="' + posturl + '" style="background:url(' + no_image_url + ') no-repeat center center;background-size: cover"/>'
                                }
                                htmlcode += '<li><span class="p-tag"></span><div class="wid-thumb">' + thumb + '</div><div class="p-head"><h3 class="wrp-titulo"><a href="' + posturl + '">' + posttitle + '</a></h3><span class="p-date">' + date + '</span></div></li>'
                            }
                            htmlcode += '</ul><div class="clear"/>';
                            $('.ready-widget .HTML .widget-content').each(function() {
                                if ($(this).text().match('feat1')) {
                                    $(this).html(htmlcode);
                                    $(this).parent().addClass('top-ideias-widget');
                                    $(this).find('.rcp-thumb').each(function() {
                                        $(this).attr('style', function(i, src) {
                                            return src.replace('/default.jpg', '/mqdefault.jpg')
                                        }).attr('style', function(i, src) {
                                            return src.replace('s72-c', 's1600')
                                        })
                                    });
                                    $("p.trans").each(function() {
                                        var e = $(this).text();
                                        var t = $(this).attr("data-tran");
                                        $("#pages-wrapper *").replaceText(e, t)
                                    })
                                }
                            })
                        }
                    })
                }
            })
        }
        if (style.match('feat2')) {
            $.ajax({
                url: "/feeds/posts/default?alt=json-in-script",
                type: 'get',
                dataType: "jsonp",
                success: function(datax) {
                    $.ajax({
                        url: "/feeds/posts/default/-/" + g + "?alt=json-in-script&max-results=" + l,
                        type: 'get',
                        dataType: "jsonp",
                        success: function(data) {
                            var posturl = "";
                            var htmlcode = '<ul class="roma-widget">';
                            for (var i = 0; i < data.feed.entry.length; i++) {
                                for (var j = 0; j < data.feed.entry[i].link.length; j++) {
                                    if (data.feed.entry[i].link[j].rel == "alternate") {
                                        posturl = data.feed.entry[i].link[j].href;
                                        break
                                    }
                                }
                                var posttitle = data.feed.entry[i].title.$t;
                                var postlabel = data.feed.entry[i].category[0].term;
                                var get_date = data.feed.entry[i].published.$t,
                                    year = get_date.substring(0, 4),
                                    month = get_date.substring(5, 7),
                                    day = get_date.substring(8, 10),
                                    date = text_month[parseInt(month, 10)] + ' ' + day + ', ' + year;
                                var tag = data.feed.entry[i].category[0].term;
                                var content = data.feed.entry[i].content.$t;
                                var $content = $('<div>').html(content);
                                if (i == 0) {
                                    var re = /<\S[^>]*>/g;
                                    var postcontent = content.replace(re, "");
                                    if (postcontent.length > 150) {
                                        postcontent = '' + postcontent.substring(0, 200) + '...'
                                    }
                                }
                                if (content.indexOf("http://www.youtube.com/embed/") > -1 || content.indexOf("https://www.youtube.com/embed/") > -1) {
                                    var src2 = data.feed.entry[i].media$thumbnail.url;
                                    var thumb = '<a class="foot-mag-thumb" href="' + posturl + '" style="background:url(' + src2 + ') no-repeat center center;background-size: cover"/>'
                                } else if (content.indexOf("<img") > -1) {
                                    var src = $content.find('img:first').attr('src');
                                    var thumb = '<a class="foot-mag-thumb" href="' + posturl + '" style="background:url(' + src + ') no-repeat center center;background-size: cover"/>'
                                } else {
                                    var thumb = '<a class="foot-mag-thumb" href="' + posturl + '" style="background:url(' + no_image_url + ') no-repeat center center;background-size: cover"/>'
                                }
                                htmlcode += '<li><span class="p-tag">' + postlabel + '</span><div class="wid-thumb">' + thumb + '</div><div class="p-head"><h3 class="wrp-titulo"><a href="' + posturl + '">' + posttitle + '</a></h3><span class="p-date">' + date + '</span><p class="recent-des">' + postcontent + '</p></div></li>'
                            }
                            htmlcode += '</ul><div class="clear"/>';
                            $('.ready-widget .HTML .widget-content').each(function() {
                                if ($(this).text().match('feat2')) {
                                    $(this).html(htmlcode);
                                    $(this).parent().addClass('content-ideias-widget');
                                    $(this).find('.rcp-thumb').each(function() {
                                        $(this).attr('style', function(i, src) {
                                            return src.replace('/default.jpg', '/mqdefault.jpg')
                                        }).attr('style', function(i, src) {
                                            return src.replace('s72-c', 's1600')
                                        })
                                    });
                                    $("p.trans").each(function() {
                                        var e = $(this).text();
                                        var t = $(this).attr("data-tran");
                                        $("#pages-wrapper *").replaceText(e, t)
                                    })
                                }
                            })
                        }
                    })
                }
            })
        }
        if (style.match('carousel')) {
            $.ajax({
                url: "/feeds/posts/default?alt=json-in-script",
                type: 'get',
                dataType: "jsonp",
                success: function(datax) {
                    $.ajax({
                        url: "/feeds/posts/default/-/" + g + "?alt=json-in-script&max-results=" + l,
                        type: 'get',
                        dataType: "jsonp",
                        success: function(data) {
                            var posturl = "";
                            var htmlcode = '<div class="main-carousel">';
                            for (var i = 0; i < data.feed.entry.length; i++) {
                                for (var j = 0; j < data.feed.entry[i].link.length; j++) {
                                    if (data.feed.entry[i].link[j].rel == "alternate") {
                                        posturl = data.feed.entry[i].link[j].href;
                                        break
                                    }
                                }
                                var posttitle = data.feed.entry[i].title.$t;
                                var author = data.feed.entry[i].author[0].name.$t;
                                var postlabel = data.feed.entry[i].category[0].term;
                                var get_date = data.feed.entry[i].published.$t,
                                    year = get_date.substring(0, 4),
                                    month = get_date.substring(5, 7),
                                    day = get_date.substring(8, 10),
                                    date = text_month[parseInt(month, 10)] + ' ' + day + ', ' + year;
                                var tag = data.feed.entry[i].category[0].term;
                                var content = data.feed.entry[i].content.$t;
                                var $content = $('<div>').html(content);
                                if (i == 0) {
                                    var re = /<\S[^>]*>/g;
                                    var postcontent = content.replace(re, "");
                                    if (postcontent.length > 150) {
                                        postcontent = '' + postcontent.substring(0, 200) + '...'
                                    }
                                }
                                if (content.indexOf("http://www.youtube.com/embed/") > -1 || content.indexOf("https://www.youtube.com/embed/") > -1) {
                                    var src2 = data.feed.entry[i].media$thumbnail.url;
                                    var thumb = '<a class="foot-mag-thumb" href="' + posturl + '" style="background:url(' + src2 + ') no-repeat center center;background-size: cover"/>'
                                } else if (content.indexOf("<img") > -1) {
                                    var src = $content.find('img:first').attr('src');
                                    var thumb = '<a class="box-image" href="' + posturl + '" style="background:url(' + src + ') no-repeat center center;background-size: cover"><span class="carousel-overlay"/></a>'
                                } else {
                                    var thumb = '<a class="box-image" href="' + posturl + '" style="background:url(' + no_image_url + ') no-repeat center center;background-size: cover"><span class="carousel-overlay"/></a>'
                                }
                                htmlcode += '<li class="carousel-item"><div class="wid-thumb">' + thumb + '</div><div class="carousel-content"><div class="carousel-tag"><a class="icon ' + postlabel + '" href="/search/label/' + postlabel + '">' + postlabel + '</a></div><h3 class="recent-title"><a href="' + posturl + '">' + posttitle + '</a></h3><span class="recent-author">' + author + '</span><span class="recent-date">' + date + '</span></div></li>'
                            }
                            htmlcode += '</div><div class="clear"/>';
                            $('.ready-widget .HTML .widget-content').each(function() {
                                if ($(this).text().match('carousel')) {
                                    $(this).html(htmlcode);
                                    $(this).parent().addClass('carousel');
                                    $(".main-carousel").owlCarousel({
                                        items: 2,
                                        smartSpeed: 550,
                                        nav: true,
                                        navText: ["", ""],
                                        loop: true,
                                        autoplay: true,
                                        autoplaySpeed: 800,
                                        dots: false,
                                        responsive: {
                                            0: {
                                                items: 1,
                                                nav: true
                                            },
                                            601: {
                                                items: 2,
                                                nav: true
                                            }
                                        }
                                    });
                                    $(this).find('.rcp-thumb').each(function() {
                                        $(this).attr('style', function(i, src) {
                                            return src.replace('/default.jpg', '/mqdefault.jpg')
                                        }).attr('style', function(i, src) {
                                            return src.replace('s72-c', 's1600')
                                        })
                                    });
                                    $("p.trans").each(function() {
                                        var e = $(this).text();
                                        var t = $(this).attr("data-tran");
                                        $("#pages-wrapper *").replaceText(e, t)
                                    })
                                }
                            })
                        }
                    })
                }
            })
        }
    })
});
$("#related-posts").each(function() {
    var g = $(this).html();
    $.ajax({
        url: "/feeds/posts/default/-/" + g + "?alt=json-in-script&max-results=" + related_number,
        type: 'get',
        dataType: "jsonp",
        success: function(data) {
            var posturl = "";
            var htmlcode = '<div class="related">';
            for (var i = 0; i < data.feed.entry.length; i++) {
                for (var j = 0; j < data.feed.entry[i].link.length; j++) {
                    if (data.feed.entry[i].link[j].rel == "alternate") {
                        posturl = data.feed.entry[i].link[j].href;
                        break
                    }
                }
                var posttitle = data.feed.entry[i].title.$t;
                var postlabel = data.feed.entry[i].category[0].term;
                var get_date = data.feed.entry[i].published.$t,
                    year = get_date.substring(0, 4),
                    month = get_date.substring(5, 7),
                    day = get_date.substring(8, 10),
                    date = text_month[parseInt(month, 10)] + ' ' + day + ', ' + year;
                var content = data.feed.entry[i].content.$t;
                var $content = $('<div>').html(content);
                if (content.indexOf("http://www.youtube.com/embed/") > -1 || content.indexOf("https://www.youtube.com/embed/") > -1) {
                    var src2 = data.feed.entry[i].media$thumbnail.url;
                    var thumb = '<a class="related-img" href="' + posturl + '" style="background:url(' + src2 + ') no-repeat center center;background-size: cover"/>'
                } else if (content.indexOf("<img") > -1) {
                    var src = $content.find('img:first').attr('src');
                    var thumb = '<a class="related-img" href="' + posturl + '" style="background:url(' + src + ') no-repeat center center;background-size: cover"><span class="related-overlay"></span></a>'
                } else {
                    var thumb = '<a class="related-img" href="' + posturl + '" style="background:url(' + no_image_url + ') no-repeat center center;background-size: cover"><span class="related-overlay"></span></a>'
                }
                htmlcode += '<li><span class="related-tag">' + postlabel + '</span><div class="related-thumb">' + thumb + '</div><div class="related-content"><h3 class="related-title"><a href="' + posturl + '">' + posttitle + '</a><span class="recent-date">' + date + '</span></h3></div></li>'
            }
            htmlcode += '</div><div class="clear"/>';
            $("#related-posts").html(htmlcode);
            $('.related-img').each(function() {
                $(this).attr('style', function(i, src) {
                    return src.replace('/default.jpg', '/mqdefault.jpg')
                }).attr('style', function(i, src) {
                    return src.replace('s72-c', 's1600')
                })
            })
        }
    })
});
