var summary = 260

function stripTags(e, t) {
    if (e.indexOf("<") != -1) {
        var n = e.split("<");
        for (var r = 0; r < n.length; r++) {
            if (n[r].indexOf(">") != -1) {
                n[r] = n[r].substring(n[r].indexOf(">") + 1, n[r].length)
            }
        }
        e = n.join("")
    }
    t = t < e.length - 1 ? t : e.length - 2;
    while (e.charAt(t - 1) != " " && e.indexOf(" ", t) != -1) t++;
    e = e.substring(0, t - 1);
    return e + "."
}

function rm(a, b, c) {
    var p = document.getElementById(a);
    var summ = summary;
    imgtag = "";
    img = p.getElementsByTagName("img");
    var q = document.getElementById(b);
    var r = document.getElementById(c);
    if (img.length >= 1) imgtag = '<a href="' + y + '"><img src="' + img[0].src + '" style="max-width:100%;" alt="' + x + '" /></a>';
    else imgtag = '<div class="no-image"/>';
    q.innerHTML = imgtag;
    r.innerHTML = '<p style="margin:0;">' + stripTags(p.innerHTML, summ) + '</p>';
    p.parentNode.removeChild(p)
}

