/*!

Name: Reading Time
Dependencies: jQuery
Author: Michael Lynch
Author URL: http://michaelynch.com
Date Created: August 14, 2013
Date Updated: July 11, 2016
Licensed under the MIT license

*/

;(function($) {

    $.fn.readingTime = function(options) {

		//define default parameters
        var defaults = {
	        readingTimeTarget: '.eta',
	        wordCountTarget: null,
	        wordsPerMinute: 270,
	        round: true,
	        lang: 'en',
			lessThanAMinuteString: '',
			prependTimeString: '',
			prependWordString: '',
	        remotePath: null,
	        remoteTarget: null,
	        success: function() {},
	        error: function() {}
        },
        plugin = this,
        el = $(this);

        //merge defaults and options
        plugin.settings = $.extend({}, defaults, options);

        //define vars
        var s = plugin.settings;

        //if no element was bound
		if(!this.length) {

			//run error callback
			s.error.call(this);

			//return so chained events can continue
			return this;
		}

        //if s.lang is set to italian
        if(s.lang == 'it') {

        	var lessThanAMinute = s.lessThanAMinuteString || "Meno di un minuto";

        	var minShortForm = 'min';

        //if s.lang is set to french
        } else if(s.lang == 'fr') {

        	var lessThanAMinute = s.lessThanAMinuteString || "Moins d'une minute";

        	var minShortForm = 'min';

	    //if s.lang is set to german
        } else if(s.lang == 'de') {

	        var lessThanAMinute = s.lessThanAMinuteString || "Weniger als eine Minute";

	        var minShortForm = 'min';

        //if s.lang is set to spanish
        } else if(s.lang == 'es') {

	        var lessThanAMinute = s.lessThanAMinuteString || "Menos de un minuto";

	        var minShortForm = 'min';

        //if s.lang is set to dutch
        } else if(s.lang == 'nl') {

	        var lessThanAMinute = s.lessThanAMinuteString || "Minder dan een minuut";

	        var minShortForm = 'min';

		//if s.lang is set to slovak
        } else if(s.lang == 'sk') {

	        var lessThanAMinute = s.lessThanAMinuteString || "Menej než minútu";

	        var minShortForm = 'min';

		//if s.lang is set to czech
        } else if(s.lang == 'cz') {

	        var lessThanAMinute = s.lessThanAMinuteString || "Méně než minutu";

	        var minShortForm = 'min';

		//if s.lang is set to Hungarian
        } else if(s.lang == 'hu') {

	        var lessThanAMinute = s.lessThanAMinuteString || "Kevesebb mint egy perc";

	        var minShortForm = 'perc';

    // if s.lang is set to Russian
        } else if (s.lang == 'ru') {

          var lessThanAMinute = s.lessThanAMinuteString || "Меньше минуты";

          var minShortForm = 'мин';

	    //if s.lang is set to Arabic
        } else if (s.lang == 'ar') {

          var lessThanAMinute = s.lessThanAMinuteString || "أقل من دقيقة";

          var minShortForm = 'دقيقة';

        //if s.lang is set to Danish
        } else if(s.lang == 'da') {
            var lessThanAMinute = s.lessThanAMinuteString || "Mindre end et minut";
            var minShortForm = 'min';

        //if s.lang is set to Icelandic
        } else if(s.lang == 'is') {
            var lessThanAMinute = s.lessThanAMinuteString || "Minna en eina mínútu";
            var minShortForm = 'min';

        //if s.lang is set to Norwegian
        } else if(s.lang == 'no') {
            var lessThanAMinute = s.lessThanAMinuteString || "Mindre enn ett minutt";
            var minShortForm = 'min';

        //if s.lang is set to Polish
        } else if(s.lang == 'pl') {
            var lessThanAMinute = s.lessThanAMinuteString || "Mniej niż minutę";
            var minShortForm = 'min';

        //if s.lang is set to Russian
        } else if(s.lang == 'ru') {
            var lessThanAMinute = s.lessThanAMinuteString || "Меньше минуты";
            var minShortForm = 'мой';

        //if s.lang is set to Swedish
        } else if(s.lang == 'sv') {
            var lessThanAMinute = s.lessThanAMinuteString || "Mindre än en minut";
            var minShortForm = 'min';

	    //default s.lang is english
        } else {

	        var lessThanAMinute = s.lessThanAMinuteString || 'Less than a minute';

	        var minShortForm = 'min';

        }

        var setTime = function(text) {

        	if(text !== '') {

		        //split text by spaces to define total words
				var totalWords = text.trim().split(/\s+/g).length;

				//define words per second based on words per minute (s.wordsPerMinute)
				var wordsPerSecond = s.wordsPerMinute / 60;

				//define total reading time in seconds
				var totalReadingTimeSeconds = totalWords / wordsPerSecond;

				//define reading time in minutes
				//if s.round is set to true
				if(s.round === true) {

					var readingTimeMinutes = Math.round(totalReadingTimeSeconds / 60);

				//if s.round is set to false
				} else {

					var readingTimeMinutes = Math.floor(totalReadingTimeSeconds / 60);

				}

				//define remaining reading time seconds
				var readingTimeSeconds = Math.round(totalReadingTimeSeconds - readingTimeMinutes * 60);

				//if s.round is set to true
				if(s.round === true) {

					//if minutes are greater than 0
					if(readingTimeMinutes > 0) {

						//set reading time by the minute
						$(s.readingTimeTarget).text(s.prependTimeString + readingTimeMinutes + ' ' + minShortForm);

					} else {

						//set reading time as less than a minute
						$(s.readingTimeTarget).text(s.prependTimeString + lessThanAMinute);

					}

				//if s.round is set to false
				} else {

					//format reading time
					var readingTime = readingTimeMinutes + ':' + readingTimeSeconds;

					//set reading time in minutes and seconds
					$(s.readingTimeTarget).text(s.prependTimeString + readingTime);

				}

				//if word count container isn't blank or undefined
				if(s.wordCountTarget !== '' && s.wordCountTarget !== undefined) {

					//set word count
					$(s.wordCountTarget).text(s.prependWordString + totalWords);

				}

				//run success callback
				s.success.call(this);

			} else {

				//run error callback
				s.error.call(this, 'The element is empty.');
			}

		};

		//for each element
		el.each(function() {

	        //if s.remotePath and s.remoteTarget aren't null
	        if(s.remotePath != null && s.remoteTarget != null) {

	        	//get contents of remote file
	    		$.get(s.remotePath, function(data) {

					//set time using the remote target found in the remote file
					setTime($('<div>').html(data).find(s.remoteTarget).text());

				});

	        } else {

		        //set time using the targeted element
		        setTime(el.text());
	        }
        });
    }

})(jQuery);


/*
* Scrolline.js - Create an indication bar line of scroll position
* Basic usage : $.scrolline();
* ---
* Version: 1.0
* Copyright 2014, Anthony Ly (http://anthonyly.com)
* Released under the MIT Licence
*/

(function($, window, document, undefined) {
    $.extend({
        scrolline: function (options) {
            var defaults = {
                direction   : 'horizontal',
                frontColor  : '#444',
                opacity     : 1,
                position    : 'top',
                reverse     : false,
                weight      : 5,
                zindex      : 10,
                scrollEnd   : function() {}
            };

            function Plugin(options) {
                this.params = $.extend(defaults, options);
                this.$back = $(document.createElement('div'));
                this.$front = $(document.createElement('div'));
                this.init();
            }

            Plugin.prototype = {
                init : function() {
                    var self = this,
                        tBack, rBack, bBack, lBack, bgBack,
                        tFront, rFront, bFront, lFront, bgFront;

                    // Direction and position
                    if(self.params.direction != 'vertical') self.params.direction = 'horizontal';
                    if(self.params.direction == 'vertical' && self.params.position != 'right') self.params.position = 'left';
                    if(self.params.direction == 'horizontal' && self.params.position != 'bottom') self.params.position = 'top';

                    if(self.params.direction == 'vertical') {
                        bBack = tBack = 0;
                        if(self.params.position == 'right') {
                            rBack = 0;
                            lBack = 'auto';
                        } else {
                            rBack = 'auto';
                            lBack = 0;
                        }
                    } else {
                        rBack = lBack = 0;
                        if(self.params.position == 'bottom') {
                            tBack = 'auto';
                            bBack = 0;
                        } else {
                            tBack =  0;
                            bBack = 'auto';
                        }
                    }

                    if(self.params.reverse && self.params.reverse === true) {
                        if(self.params.direction == 'vertical') {
                            bFront = rFront = lFront = 0;
                            tFront = 'auto';
                        } else {
                            bFront = rFront = rFront = 0;
                            lFront = 'auto';
                        }
                    } else {
                        tFront = lFront = 0;
                        bFront = rFront = 'auto';
                    }

                    self.$front.css({
                        background : self.params.frontColor,
                        bottom : bFront,
                        height : 0,
                        left : lFront,
                        margin: 0,
                        overflow: 'hidden',
                        padding: 0,
                        position: 'absolute',
                        right : rFront,
                        top: tFront,
                        width : 0
                    }).appendTo(self.$back);

                    self.$back.css({
                        background : self.params.backColor,
                        bottom: bBack,
                        height : 0,
                        left : lBack,
                        opacity: self.params.opacity,
                        margin: 0,
                        overflow: 'hidden',
                        position: 'fixed',
                        padding: 0,
                        right : rBack,
                        top: tBack,
                        width : 0,
                        zIndex : self.params.zindex,
                    }).appendTo('body');

                    $(window).on("load resize scroll orientationchange", function() {
                        self.scrollListener();
                    });
                },

                scrollListener : function() {
                    var self = this,
                        hWin = $(window).height(),
                        wWin = $(window).width(),
                        hDoc = $(document).height(),
                        scrollValue = $(window).scrollTop(),
                        wBack, hBack, wFront, hFront, scrollineVal, wRef;

                    if(self.params.direction == 'vertical') {
                        scrollineVal = (scrollValue + hWin) * hWin / hDoc;
                        wBack = self.params.weight;
                        hBack = wRef = hWin;
                        wFront = self.params.weight;
                        hFront = scrollineVal;
                    } else {
                        scrollineVal = (scrollValue + hWin) * wWin / hDoc;
                        wBack = wRef = wWin;
                        hBack = self.params.weight;
                        wFront = scrollineVal;
                        hFront = self.params.weight;
                    }

                    self.$back.css({
                        height: hBack,
                        width: wBack
                    });
                    self.$front.css({
                        height: hFront,
                        width: wFront
                    });

                    if(scrollineVal >= wRef) {
                        self.params.scrollEnd();
                    }
                }
            };

            new Plugin(options);
        }
    });
})(jQuery, window, document);







