 $(document).ready(function(){
  var ost = 0;

  $(window).scroll(function() {
    var cOst = $(this).scrollTop();

    if(cOst > ost) {
       $('#outer-wrapper').addClass('fixed23').removeClass('default23');
    }
    else {
       $('#outer-wrapper').addClass('default23').removeClass('fixed23');
    }

    ost = cOst;
  });

});

