html.script:
 //
var classicMode = false ;
var summary = 20;
var indent = 3;
imgr = new Array();
imgr[0] = "http://sites.google.com/site/fdblogsite/Home/nothumbnail.gif";
showRandomImg = true;
aBold = true;
summaryPost = 170; 
summaryTitle = 25; 
numposts1 = 6; 
numposts2 = 8;
var classicMode = false ;
var summary = 50;
var indent = 3;
var relatedTitles = new Array();
var relatedTitlesNum = 0;
var relatedUrls = new Array();
var thumburl = new Array();

function stripHtmlTags(s,max){return s.replace(/<.*?>/ig, '').split(/\s+/).slice(0,max-1).join(' ')}

function createSummaryAndThumb(pID, title, url, date, comment, tag, author) {
    var posturl = url;
    var title = title;
    var date = date;
    var comment = comment;
    var tag = tag;
    var div = document.getElementById(pID);
    var img = div.getElementsByTagName("img");
    var content1 = div.innerHTML.replace(/<img.*?>/ig, '').replace(/<iframe.*?>/ig, '');
    var arr = content1.split(/<br\s*\/?>/);
    var content = arr[0] + arr.slice(1, -1).join('<br>') + arr.slice(-1);
    var month = new Array();
    month[0] = "Jan";
    month[1] = "Feb";
    month[2] = "Mar";
    month[3] = "Apr";
    month[4] = "May";
    month[5] = "Jun";
    month[6] = "Jul";
    month[7] = "Aug";
    month[8] = "Sep";
    month[9] = "Oct";
    month[10] = "Nov";
    month[11] = "Dec";
    var n = month[date.split('/')[0]];
    var date1 = date.split('/')[1];
    var year = date.split('/')[2];
    if (img.length == 1) {
        var imgurl = img[0].src;
        var thumb = '<div class="post-image"><a href="' + posturl + '"><img width="940" height="987" src="' + imgurl + '"></a></div>';
        var summary1 = thumb + '<div class="post-entry">' + stripHtmlTags(content, 50) + '...<p></div> <a class="more-link" href="' + posturl + '">Read More...</a></p>'
    } else {
        if (img.length > 1) {
            var li = '';
            for (var i = 0; i < img.length; i++) {
                var li = li + '<li><img src="' + img[i].src + '"></li>'
            }
            var thumb = '<div class="post-image"><div class="bx-wrapper"><ul class="bxslider">' + li + '</ul></div></div>';
            var summary1 = thumb + '<div class="post-entry">' + stripHtmlTags(content, 50) + '...<p> </div><a class="more-link" href="' + posturl + '">Read More...</a></p>'
        } else {
            var frame = div.getElementsByTagName("iframe");
            if (frame.length >= 1) {
                var iframe1 = frame[0].src;
                var thumb = '<div class="post-image"><iframe width="100%" height="450" frameborder="no" src="' + iframe1 + '" scrolling="no"></iframe></div>';
                var summary1 = thumb + '<div class="post-entry">' + stripHtmlTags(content, 50) + '...<p></div> <a class="more-link" href="' + posturl + '">Read More...</a></p>'
            } else {
                var summary1 = '<div class="post-entry">' + stripHtmlTags(content, 50) + '...<p> </div><a class="more-link" href="' + posturl + '">Read More...</a></p>'
            }
        }
    }
    div.innerHTML = summary1;
    div.style.display = "block";
    var elem = document.getElementsByClassName("separator");
    for (var i = 0; i < elem.length; i++) {
        elem[i].innerHTML = ''
    }
}

function related_results_labels_thumbs(json) {
    for (var i = 0; i < json.feed.entry.length; i++) {
        var entry = json.feed.entry[i];
        relatedTitles[relatedTitlesNum] = entry.title.$t;
        try {
            thumburl[relatedTitlesNum] = entry.media$thumbnail.url
        } catch (error) {
            s = entry.content.$t;
            a = s.indexOf("<img");
            b = s.indexOf("src=\"", a);
            c = s.indexOf("\"", b + 5);
            d = s.substr(b + 5, c - b - 5);
            if ((a != -1) && (b != -1) && (c != -1) && (d != "")) {
                thumburl[relatedTitlesNum] = d
            } else {
                if (typeof(defaultnoimage) !== 'undefined') thumburl[relatedTitlesNum] = defaultnoimage;
                else thumburl[relatedTitlesNum] = "http://1.bp.blogspot.com/_u4gySN2ZgqE/SosvnavWq0I/AAAAAAAAArk/yL95WlyTqr0/s400/noimage.png"
            }
        }
        if (relatedTitles[relatedTitlesNum].length > 35) relatedTitles[relatedTitlesNum] = relatedTitles[relatedTitlesNum].substring(0, 35) + "...";
        for (var k = 0; k < entry.link.length; k++) {
            if (entry.link[k].rel == 'alternate') {
                relatedUrls[relatedTitlesNum] = entry.link[k].href;
                relatedTitlesNum++
            }
        }
    }
}

function removeRelatedDuplicates_thumbs() {
    var tmp = new Array(0);
    var tmp2 = new Array(0);
    var tmp3 = new Array(0);
    for (var i = 0; i < relatedUrls.length; i++) {
        if (!contains_thumbs(tmp, relatedUrls[i])) {
            tmp.length += 1;
            tmp[tmp.length - 1] = relatedUrls[i];
            tmp2.length += 1;
            tmp3.length += 1;
            tmp2[tmp2.length - 1] = relatedTitles[i];
            tmp3[tmp3.length - 1] = thumburl[i]
        }
    }
    relatedTitles = tmp2;
    relatedUrls = tmp;
    thumburl = tmp3
}

function contains_thumbs(a, e) {
    for (var j = 0; j < a.length; j++)
        if (a[j] == e) return true;
    return false
}

function printRelatedLabels_thumbs(current) {
    for (var i = 0; i < relatedUrls.length; i++) {
        if ((relatedUrls[i] == current) || (!relatedTitles[i])) {
            relatedUrls.splice(i, 1);
            relatedTitles.splice(i, 1);
            thumburl.splice(i, 1);
            i--
        }
    }
    var r = Math.floor((relatedTitles.length - 1) * Math.random());
    var i = 0;
    while (i < relatedTitles.length && i < 20 && i < maxresults) {
        tmb = thumburl[r].replace('s72-c/', 's300-c/');
        document.write('<div class="item-related"><a href="' + relatedUrls[r] + '"><img width="150" height="100" src="' + tmb + '"/></a><h3><a href="' + relatedUrls[r] + '">' + relatedTitles[r] + '</a></h3></div>');
        i++;
        if (r < relatedTitles.length - 1) {
            r++
        } else {
            r = 0
        }
    }
    relatedUrls.splice(0, relatedUrls.length);
    thumburl.splice(0, thumburl.length);
    relatedTitles.splice(0, relatedTitles.length)
}


function removeHtmlTag(strx,chop){
	var s = strx.split("<");
	for(var i=0;i<s.length;i++){
		if(s[i].indexOf(">")!=-1){
			s[i] = s[i].substring(s[i].indexOf(">")+1,s[i].length);
		}
	}
	s =  s.join("");
	s = s.substring(0,chop-1);
	return s;
}
