            document.write(trtd)
        } else if (i == 1) {
            var trtd = '<div class="secondary-post col-post" style="margin-right:0"><a class="hover_play_small" href="' + posturl + '"><img src="' + img[i] + '" height="200" width="320"></img></a><header><span class="blue">' + tag + '</span><h4><a href="' + posturl + '">' + posttitle + "</a></h4><div class='date-l'><i class='fa fa-clock-o'></i>" + daystr + "</div><div class='date-r'><i class='fa fa-comments'></i>" + pcm + "</div></header></div>";
            document.write(trtd)
        } else if (i == 2) {
            var trtd = '<div class="secondary-post col-post" style="margin-right:0"><a class="hover_play_small" href="' + posturl + '"><img src="' + img[i] + '" height="200" width="320"></img></a><header><span class="green">' + tag + '</span><h4><a href="' + posturl + '">' + posttitle + "</a></h4><div class='date-l'><i class='fa fa-clock-o'></i>" + daystr + "</div><div class='date-r'><i class='fa fa-comments'></i>" + pcm + "</div></header></div>";
