        var relatedTitles = new Array();
    var relatedTitlesNum = 0;
    var relatedUrls = new Array();
    var thumburl = new Array();

    function related_results_labels_thumbs(json) {
    	for (var i = 0; i < json.feed.entry.length; i++) {
    		var entry = json.feed.entry[i];
    		relatedTitles[relatedTitlesNum] = entry.title.$t;
    		try {
    			thumburl[relatedTitlesNum] = entry.gform_foot.url
    		} catch (error) {
    			s = entry.content.$t;
    			a = s.indexOf("<img");
    			b = s.indexOf("src=\"", a);
    			c = s.indexOf("\"", b + 5);
    			d = s.substr(b + 5, c - b - 5);
    			if ((a != -1) && (b != -1) && (c != -1) && (d != "")) {
    				thumburl[relatedTitlesNum] = d
    			} else thumburl[relatedTitlesNum] = 'http://3.bp.blogspot.com/-zP87C2q9yog/UVopoHY30SI/AAAAAAAAE5k/AIyPvrpGLn8/s1600/picture_not_available.png'
    		}
    		if (relatedTitles[relatedTitlesNum].length > 35) relatedTitles[relatedTitlesNum] = relatedTitles[relatedTitlesNum].substring(0, 35) + "...";
    		for (var k = 0; k < entry.link.length; k++) {
    			if (entry.link[k].rel == 'alternate') {
    				relatedUrls[relatedTitlesNum] = entry.link[k].href;
    				relatedTitlesNum++
    			}
    		}
    	}
    }
    function removeRelatedDuplicates_thumbs() {
    	var tmp = new Array(0);
    	var tmp2 = new Array(0);
    	var tmp3 = new Array(0);
    	for (var i = 0; i < relatedUrls.length; i++) {
    		if (!contains_thumbs(tmp, relatedUrls[i])) {
    			tmp.length += 1;
    			tmp[tmp.length - 1] = relatedUrls[i];
    			tmp2.length += 1;
    			tmp3.length += 1;
    			tmp2[tmp2.length - 1] = relatedTitles[i];
    			tmp3[tmp3.length - 1] = thumburl[i]
    		}
    	}
    	relatedTitles = tmp2;
    	relatedUrls = tmp;
    	thumburl = tmp3
    }
    function contains_thumbs(a, e) {
    	for (var j = 0; j < a.length; j++) if (a[j] == e) return true;
    	return false
    }
    function printRelatedLabels_thumbs() {
    	for (var i = 0; i < relatedUrls.length; i++) {
    		if ((relatedUrls[i] == currentposturl) || (!(relatedTitles[i]))) {
    			relatedUrls.splice(i, 1);
    			relatedTitles.splice(i, 1);
    			thumburl.splice(i, 1);
    			i--
    		}
    	}
    	var r = Math.floor((relatedTitles.length - 1) * Math.random());
    	var i = 0;
    	if (relatedTitles.length >= 1)

document.write('<div class="large-12 column"><h3 class="entry-title related-main">Related Articles</h3></div>');          
    	document.write('<div class="clear">');
    	while (i < relatedTitles.length && i < 20 && i < maxresults) {
    		document.write('<div class="related-item col-1-3"><a style="text-decoration:none;margin:0 7px 0px 0;');
    		if (i != 0) document.write('"');
    		else document.write('"');
    		document.write(' href="' + relatedUrls[r] + '"><img class="related_img" src="' + thumburl[r] + '"/><br/><div class="related_title">' + relatedTitles[r] + '</div></a></div>');
    		if (r < relatedTitles.length - 1) {
    			r++
    		} else {
    			r = 0
    		}
    		i++
    	}
    	document.write('</div>');
    	relatedUrls.splice(0, relatedUrls.length);
    	thumburl.splice(0, thumburl.length);
    	relatedTitles.splice(0, relatedTitles.length)
    }
