$(function() {
	$('#slideshow img').slideshowify({
		randomize    : true,
		fadeInSpeed  : 1500,
		fadeOutSpeed : 1500,
		aniSpeedMin  : 13000,
parentEl    : ".wrapper",
		aniSpeedMax  : 17000
	});
});


