head.script:
var thumbnail_mode = "no-float" ;
summary_noimg = 200;  /* Summary length if no image*/
summary_img = 100; /* Summary length with image*/

//
function removeHtmlTag(a, b) {
    if (a.indexOf("<") != -1) {
        var c = a.split("<");
        for (var d = 0; d < c.length; d++)
            if (c[d].indexOf(">") != -1) c[d] = c[d].substring(c[d].indexOf(">") + 1, c[d].length);
        a = c.join("")
    }
    b = b < a.length - 1 ? b : a.length - 2;
    while (" " != a.charAt(b - 1) && a.indexOf(" ", b) != -1) b++;
    a = a.substring(0, b - 1);
    return a + "..."
}

function createSummaryAndThumb(a) {
    var b = document.getElementById(a);
    var c = "";
    var d = b.getElementsByTagName("img");
    var e = summary_noimg;
    if (d.length >= 1);
    var f = c + "<div>" + removeHtmlTag(b.innerHTML, e) + "</div>";
    b.innerHTML = f
}

function labelthumbs(e) {
    document.write('<ul id="label_with_thumbs">');
    for (var t = 0; t < numposts; t++) {
        var n = e.feed.entry[t];
        var r = n.title.$t;
        var i;
        if (t == e.feed.entry.length) break;
        for (var o = 0; o < n.link.length; o++) {
            if (n.link[o].rel == "replies" && n.link[o].type == "text/html") {
                var u = n.link[o].title;
                var f = n.link[o].href
            }
            if (n.link[o].rel == "alternate") {
                i = n.link[o].href;
                break
            }
        }
        var l;
        try {
            l = n.media$thumbnail.url
        } catch (h) {
            s = n.content.$t;
            a = s.indexOf("<img");
            b = s.indexOf('src="', a);
            c = s.indexOf('"', b + 5);
            d = s.substr(b + 5, c - b - 5);
            if (a != -1 && b != -1 && c != -1 && d != "") {
                l = d
            } else l = "http://3.bp.blogspot.com/-zP87C2q9yog/UVopoHY30SI/AAAAAAAAE5k/AIyPvrpGLn8/s1600/picture_not_available.png"
        }
        var p = n.published.$t;
        var v = p.substring(0, 4);
        var m = p.substring(5, 7);
        var g = p.substring(8, 10);
        var y = new Array;
        y[1] = "Jan";
        y[2] = "Feb";
        y[3] = "Mar";
        y[4] = "Apr";
        y[5] = "May";
        y[6] = "June";
        y[7] = "July";
