function stripTags(s, n) {
    return s.replace(/<.*?>/ig, "").split(/\s+/).slice(0, n - 1).join(" ")
}

function readmore(a) {
    var p = document.getElementById(a),
        imgtag = "",
        ifrtag = "",
        ifrsrc = "",
        image_width = 350,
        image_height = 230,
        ifrtb = -1,
        img = p.getElementsByTagName("img"),
        ifr = p.getElementsByTagName("iframe");
    for (var i = 0; i < ifr.length; i++) {
        ifrsrc = ifr[i].src;
        if (ifrsrc.indexOf("//www.youtube.com/embed/") != -1) {
            ifrtb = i;
            break
        } else if (ifrsrc.indexOf("//player.vimeo.com/video/") != -1) {
            ifrtb = i;
            break
        } else if (ifrsrc.indexOf("//www.dailymotion.com/embed/video/") != -1) {
            ifrtb = i;
            break
        } else if (ifrsrc.indexOf("//w.soundcloud.com/player/") != -1) {
            ifrtb = i;
            break
        }
    }
    if (ifrtb != -1) {
        ifrtag = '<div class="entry-video"><iframe src="' + ifrsrc + '?vq=medium&rel=0" frameborder="0" allowfullscreen></iframe></div>';
        var summary1 = '<div class="univideo">' + ifrtag + '</div><h3 class="post-title"><a href="' + y + '">' + x + '</a></h3><div class="post_item_inner"><p>' + stripTags(p.innerHTML, 15) + '...</p></div><a href="' + y + '" class="button button--nina button--text-thick button--text-upper button--size-s" data-text="Read More"><span>R</span><span>e</span><span>a</span><span>d</span> <span>M</span><span>o</span><span>r</span><span>e</span></a>'
    } else if (img.length >= 1) {
        var imgurl = img[0].src;
        imgtag = '<div class="image-holder"><a href="' + y + '"><img width="' + image_width + '" height="' + image_height + '" src="' + imgurl.replace('/s1600/', '/w' + image_width + '-h' + image_height + '-c/') + '" style="display: inline;"/></a><div class="ts-lima-effect"><div class="hover-one"></div><div class="hover-two"></div><div class="lima-details"><a href="http://demo.touchsize.com/esquise/2015/02/06/save-clock-tower-now/" class="more-details">read more<i class="icon-right"></i></a><div class="sharing-options"><span>Share on:</span><ul><li><a class="hi-icon hi-icon-chat" href="http://www.facebook.com/sharer.php?u=' + y + '"><i class="fa fa-facebook"></i></a></li><li><a class="hi-icon hi-icon-chat" href="http://twitter.com/home?status=' + y + '"><i class="fa fa-twitter"></i></a></li><li><a class="hi-icon hi-icon-chat" href="https://plus.google.com/share?url=' + y + '"><i class="fa fa-google-plus"></i></a></li><li><a class="hi-icon hi-icon-chat" href="http://pinterest.com/pin/create/button/?url=' + y + '&amp;media=' + imgurl + '&amp;description=' + x + '&amp;title=' + x + ' " target="_blank"><i class="fa fa-pinterest"></i></a></li></ul></div></div></div></div>';
        var summary1 = '<div class="unimage">' + imgtag + '</div><h3 class="post-title"><a href="' + y + '">' + x + '</a></h3><div class="post_item_inner"><p>' + stripTags(p.innerHTML, 15) + '...</p></div><a href="' + y + '" class="button button--nina button--text-thick button--text-upper button--size-s" data-text="Read More"><span>R</span><span>e</span><span>a</span><span>d</span> <span>M</span><span>o</span><span>r</span><span>e</span></a>'
    } else imgtag = '<img class="thumb no-img" src="http://3.bp.blogspot.com/-ltyYh4ysBHI/U04MKlHc6pI/AAAAAAAADQo/PFxXaGZu9PQ/s1600/no-image.png"></img>';
    p.innerHTML = summary1
};


