        }
        if (i == g.feed.entry.length) break;
        for (var k = 0; k < n.link.length; k++) {
            if (n.link[k].rel == 'alternate') {
                t = n.link[k].href;
                break
            }
        }
        for (var k = 0; k < n.link.length; k++) {
            if (n.link[k].rel == 'replies' && n.link[k].type == 'text/html') {
