                break
            }
        }
        if ("content" in n) {
            var w = n.content.$t
        } else if ("summary" in n) {
            var w = n.summary.$t
        } else var w = "";
        postdate = n.published.$t;
        if (j > imgr.length - 1) j = 0;
        img[i] = imgr[j];
