$(document).ready(function() {
    $('.PopularPosts img').each(function() {
        $(this).attr('src', function(i, a) {
            return a.replace('s72-c', 's400-c')
        })
    });
    $('.postarea4').slick({
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        dots: true,
        autoplay: true
    });
    $('.slicktopgrid').slick({
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        dots: true,
        autoplay: false
    });
    $('.articles7').slick({
        infinite: true,
        slidesToShow: 5,
        slidesToScroll: 1,
        dots: false,
        autoplay: true,
        responsive: [{
            breakpoint: 900,
            settings: {
                slidesToShow: 4,
                slidesToScroll: 1,
                infinite: true,
                dots: false
            }
        }, {
            breakpoint: 600,
            settings: {
                slidesToShow: 2,
                slidesToScroll: 1
            }
        }, {
            breakpoint: 480,
            settings: {
                slidesToShow: 1,
                slidesToScroll: 1
            }
        }]
    });
    var b = $('<i class="fa fa-caret-down"></i>');
    var c = $('<i class="fa fa-caret-down"></i>');
    var d = $('<i class="fa fa-caret-right"></i>');
    $('#megamenuid > li:has(ul) > a').append(b);
    $('#megamenuid li:has(ul)  li:has(ul)>a').append(d);
    $('.ot-top-menu > li:has(ul) > a').append(c);
    $('.ot-top-menu li:has(ul)  li:has(ul)>a').append(d);
    $(".sb-icon-search").click(function() {
        $('div#sb-search').addClass("showsearch");
        $('.sb-icon-close').addClass("newshow")
    });
    $(".sb-icon-close").click(function() {
        $('div#sb-search').removeClass("showsearch");
        $('.sb-icon-close').removeClass("newshow")
    });
    $(".topbar").stickMe()
});
$(window).load(function() {
    setTimeout(function() {
        $("#loader").css("display", "none")
    }, 300)
});


