head.script:
var classicMode = false ;
var summary_noimg = 50;
var summary_img = 40;
var indent = 0;

//


function stripHtmlTags(s,max){return s.replace(/<.*?>/ig, '').split(/\s+/).slice(0,max-1).join(' ')}

function getSummaryLikeWP(id) {
return document.getElementById(id).innerHTML.split(/<!--\s*more\s*-->/)[0];
}

function getSummaryImproved(post,max){
var re = /<.*?>/gi
var re2 = /<br.*?>/gi
var re3 = /(<\/{1}p>)|(<\/{1}div>)/gi
var re4 = /(<style.*?\/{1}style>)|(<script.*?\/{1}script>)|(<table.*?\/{1}table>)|(<form.*?\/{1}form>)|(<code.*?\/{1}code>)|(<pre.*?\/{1}pre>)/gi

post = post.replace(re4,'')
post = post.replace(re3,'<br /> ').split(re2)

for(var i=0; i<post.length; i++){
post[i] = post[i].replace(re,'');
}
var post2 = new Array();
for(var i in post) {
//if(post[i]!='' && post[i]!=' ' && post[i] != '\n') post2.push(post[i]);
if(/[a-zA-Z0-9]/.test(post[i])) post2.push(post[i]) ;

}


var s = "<p>";
var indentBlank = "<p>";
for(var i=0;i<indent;i++){
indentBlank += " ";
}
if(post2.join('<br/>').split(' ').length < max-1 ){
s = post2.join(indentBlank +' </p>');
} else {
var i = 0;
while(s.split(' ').length < max){
s += indentBlank + ' ' + post2[i]+'</p>';
i++;
}
} 
return s;
}

function rm(a) {
    var p = document.getElementById(a);
    imgtag = "";
    ifrtag = "";
    ifrsrc = "";
    ifrtb = -1;
    img = p.getElementsByTagName("img");
    var frame = p.getElementsByTagName("iframe");
    if (img.length == 1) {
        var imgurl = img[0].src;
        var thumb = '<div class="post-image"><a href="' + y + '"><img width="940" height="987" src="' + imgurl + '"></a></div>';
        p.innerHTML = '<div class="entry-blog"><header class="page-header1"><span class="author">' + t + '</span><h2 class="page-title"><a href="' + y + '">' + x + '</a></h2><ul class="entry-meta"><li class="publish-date">Posted by <a href="' + y + '">' + z + '</a></li><li class="tags">in ' + l + '</li><li class="comments-link"><a href="' + y + '#comments">' + u + ' Comments</a> </li></ul>' + thumb + '<div class="entry-content cf">' + getSummaryImproved(p.innerHTML, 55) + '<span class="fixed-char"></span></div><div class="tag-type"><i class="fa fa fa-picture-o"></i></div>'
    } else {
        if (img.length > 1) {
            var li = '';
            for (var i = 0; i < img.length; i++) {
                var li = li + '<li><img src="' + img[i].src + '"></li>'
            }
            var thumb = '<div class="post-image"><div class="bx-wrapper"><ul class="bxslider">' + li + '</ul></div></div>';
            p.innerHTML = '<div class="entry-blog"><header class="page-header1"><span class="author">' + t + '</span><h2 class="page-title"><a href="' + y + '">' + x + '</a></h2><ul class="entry-meta"><li class="publish-date">Posted by <a href="' + y + '">' + z + '</a></li><li class="tags">in ' + l + '</li><li class="comments-link"><a href="' + y + '#comments">' + u + ' Comments</a> </li></ul>' + thumb + '<div class="entry-content cf">' + getSummaryImproved(p.innerHTML, 55) + '<span class="fixed-char"></span></div><div class="tag-type"><i class="fa fa fa-picture-o"></i></div>'
        } else {
            if (frame.length >= 1) {
                var iframe1 = frame[0].src;
                var thumb = '<div class="entry-video"><iframe width="100%" height="450" frameborder="no" src="' + iframe1 + '" scrolling="no"></iframe></div>';
                p.innerHTML = '<div class="entry-blog"><header class="page-header1"><span class="author">' + t + '</span><h2 class="page-title"><a href="' + y + '">' + x + '</a></h2><ul class="entry-meta"><li class="publish-date">Posted by <a href="' + y + '">' + z + '</a></li><li class="tags">in ' + l + '</li><li class="comments-link"><a href="' + y + '#comments">' + u + ' Comments</a> </li></ul>' + thumb + '<div class="entry-content cf">' + getSummaryImproved(p.innerHTML, 55) + '<span class="fixed-char"></span></div><div class="tag-type"><i class="fa fa-video-camera"></i></div>'
            } else {
                p.innerHTML = '<div class="entry-blog"><header class="page-header1"><span class="author">' + t + '</span><h2 class="page-title"><a href="' + y + '">' + x + '</a></h2><ul class="entry-meta"><li class="publish-date">Posted by <a href="' + y + '">' + z + '</a></li><li class="tags">in ' + l + '</li><li class="comments-link"><a href="' + y + '#comments">' + u + ' Comments</a> </li></ul><div class="entry-content cf">' + getSummaryImproved(p.innerHTML, 80) + '<span class="fixed-char"></span></div><div class="tag-type"><i class="fa fa-pencil-square-o"></i></div>'
            }
        }
    }
}

