b:if.script:
  /**/
var perPage = 7;
var numPages = 6;
var firstText = 'First';
var lastText = 'Last';
var prevText = '\xAB Previous';
var nextText = 'Next \xBB';
var urlactivepage = location['href'];
var home_page = '/';

  /**/
