      //

	  no_image_url = 'https://2.bp.blogspot.com/-0PTH--hIAHU/VsC6iFnrJII/AAAAAAAAAZg/msMKLIaI8rE/s1600/no_img_url.png';

      /* Number of posts to show for the Homepage widgets */
      twocolumnsPostsNum = 6;

