    }
    fp.html(trtd);
  } // function FeaturedPosts(e)

  if((fpContent.toLowerCase() !== 'no') && (fpContent.toLowerCase() !== '"no"') && (fpContent !== '')) {
    fp.addClass('clearfix');
    if(fpContent !== "[recent]") {
      $.ajax({
