function startPagination(a) {
    var b = '';
    pageNumber = parseInt(numOfPages / 2);
    if (pageNumber == numOfPages - pageNumber) {
        numOfPages = pageNumber * 2 + 1
    }
    pageStart = currentPageNo - pageNumber;
